INSERT INTO `mn_xz_recard` VALUES ('390', 'oV9VJ5BEmw3kpYxX1SHtJLqg14wo', '麻小', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJqAiaBV0icS6EvuvzAGs9EDiaJzKZrrIEbfZ1qFBbWO9jI0azwEM8mniaXoE1XT3xO2VcJjp6kZKUYcg/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', '这是一个非常浪漫的组合!因为两人对爱情的感觉很相像，所以相处起来也很顺利，当然两人还是有不少不同的地方，这反而会增加彼此的吸引力哦!可以算是能常常速配成功的组合! ', '鱼鱼虽然有点难以捉摸，不过你对他的好他都知道，如果再不时营造一下浪漫气氛，鱼鱼马上就会被气氛迷惑，这时候就算是求婚也没什么问题了!对于鱼鱼的迷糊狮子要多包容，对于他们的胆小狮子则要多体谅，最好让鱼鱼觉得和你在一起很幸福，那就跑不掉了。', '28', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('391', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5H-oTelIjglAZH48dZKNUN8', '悟空爱唐僧', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83erzRibxmbXZib3wHSiaUwTdaoQl8uCjvVHhC9C02iaeT2Rz0HkKC4uVsuNusZQyPeXx2SebXAzes63wwA/132', '射手', '../images/staricon/sheshou.png', '遇到射手，双鱼们最好的方式就是摆出自己的万种风情，优雅、高贵、迷人，这些特质鱼鱼都有，就算不多但在欺敌上也够了!当然同时也要把自己的呆样收拾好，只要你表现够秀，不用你主动射手就迫不及待了，记得要充分发挥自己的幽默感，这是射手们最抵挡不住的。', '这是个非常美满的组合!尤其是对喜爱浪漫的人来说是天上地下无双的配对，既浪漫又热烈的剧情全在这种配对身上，叫身边的人不眼红都不行，是适合长久经营的配对。', '74', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('392', 'oV9VJ5H-oTelIjglAZH48dZKNUN8', '悟空爱唐僧', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83erzRibxmbXZib3wHSiaUwTdaoQl8uCjvVHhC9C02iaeT2Rz0HkKC4uVsuNusZQyPeXx2SebXAzes63wwA/132', '射手', '../images/staricon/sheshou.png', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', '这个组合虽然没什么争执或火爆的场面，但是他们的确是不怎么相合的，因为两人星座一样懒，让他们没心情探究得太过仔细，所以速配的机会不大。', '遇到鱼座，射手们就得把自己的大而化之收拾一下，当个痴情的女主角或男主角，这种配合会让鱼鱼很感动，也会因为气氛的浪漫而义无反顾地跳下去，让你不费吹灰之力就拐到大笨鱼!之后，当然还要让鱼鱼尽量不要太早醒来，继续浪漫下去! ', '74', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('393', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5L961WOrwSsw0pOS-GVOVH8', '忆高山', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTIJ1kJfJUpT1EqVTNuZloNKcmfaTUXeGibjia83icDaicoicuvXKGsic1389zz7cLeM40IVXnuicyzckZ0YQ/132', '巨蟹', '../images/staricon/juxie.png', '遇到蟹子，鱼座惟一要做的就是把蟹子心底想的摸个透彻，因为蟹子很需要别人了解和谅解，而鱼座正好具有这种同情心旺盛的特质，加上似水的柔情，不用一时三刻，蟹子不上钩都难。不过也是因为这两个星座天生有互相吸引的特质，所以实在是不用动太多的脑筋。', '一个这么深情的组合还有什么好比的?不过若是在爱情的依赖度上，鱼鱼对巨蟹座有着莫名的依赖感，可能是因为巨蟹具有母亲本能吧!这种包容和体贴，是鱼座软弱时最好的避风港，鱼鱼其实是很脆弱的，受不得大风大浪，蟹子的两个大螯正好提供了他们最好的保证。', '83', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('394', 'oV9VJ5L961WOrwSsw0pOS-GVOVH8', '忆高山', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTIJ1kJfJUpT1EqVTNuZloNKcmfaTUXeGibjia83icDaicoicuvXKGsic1389zz7cLeM40IVXnuicyzckZ0YQ/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', '这可是一个超级浪漫的组合，他们不切实际的程度足以媲美\"灰姑娘\"的际遇，这个配对真的很容易变得不切实际，有可能有了爱情就不要面包，不过却是非常非常的惟美浪漫哦! ', '蟹子的全心付出会让鱼座非常感动，觉得无以为报，所以蟹子要做的事就是不断付出自己丰沛的爱心，让鱼座溺死在里面，连翻身的机会都没有，加上鱼座对巨蟹日渐变得依赖，这样一来鱼鱼就再也跑不掉了，而这个美妙的爱情配对，自然就会有幸福美满的结局出现了! ', '83', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('395', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5MQlxE7k_sweVdqaPhU6mK8', '东', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKq7AIbRpKD0lkib2LvXgGrva9qwkkzCUmhxdkFQemzg5VBwtf0b5RdBI7S4zWWCKx2Veee2iaWOl8w/132', '狮子', '../images/staricon/shizi.png', '遇到狮子座鱼座只要祭出自己细心体贴外加柔情似水的法宝，保证狮座招架不住，连战连败。想要什么只要鱼鱼轻声细语一番，狮子立刻奉如圣旨；当然狮子生气时，这招再加上眼泪就足以让他们有气变没气，反而自己做错事似地不断赔罪，鱼座只要暗中偷笑即可。', '这个组合充满了戏剧化的浪漫气氛。两人在一些小事情上特别来电，而且对于对方的心情也相当了解，相处上更充满了浪漫的气息，常常会出现如在小说漫画中的情节，让人赞叹不已。', '22', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('396', 'oV9VJ5MQlxE7k_sweVdqaPhU6mK8', '东', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKq7AIbRpKD0lkib2LvXgGrva9qwkkzCUmhxdkFQemzg5VBwtf0b5RdBI7S4zWWCKx2Veee2iaWOl8w/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', '这是一个非常浪漫的组合!因为两人对爱情的感觉很相像，所以相处起来也很顺利，当然两人还是有不少不同的地方，这反而会增加彼此的吸引力哦!可以算是能常常速配成功的组合! ', '鱼鱼虽然有点难以捉摸，不过你对他的好他都知道，如果再不时营造一下浪漫气氛，鱼鱼马上就会被气氛迷惑，这时候就算是求婚也没什么问题了!对于鱼鱼的迷糊狮子要多包容，对于他们的胆小狮子则要多体谅，最好让鱼鱼觉得和你在一起很幸福，那就跑不掉了。', '22', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('397', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5CNmtpiLKUzOxdI2nM8TTi8', '纤画-刘卓(漆艺)', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTL7v0htO417V1dhaNbhwp0uZBIsicwAKhhXJvs0icEjIUCYTsdvb4nibicOiam6ebicdDOwCu8EwCYemJTA/132', '双鱼', '../images/staricon/shuangyu.png', '这可是一个超级浪漫的组合，他们不切实际的程度足以媲美\"灰姑娘\"的际遇，这个配对真的很容易变得不切实际，有可能有了爱情就不要面包，不过却是非常非常的惟美浪漫哦! ', '蟹子的全心付出会让鱼座非常感动，觉得无以为报，所以蟹子要做的事就是不断付出自己丰沛的爱心，让鱼座溺死在里面，连翻身的机会都没有，加上鱼座对巨蟹日渐变得依赖，这样一来鱼鱼就再也跑不掉了，而这个美妙的爱情配对，自然就会有幸福美满的结局出现了! ', '99', '0', '1');
INSERT INTO `mn_xz_recard` VALUES ('398', 'oV9VJ5CNmtpiLKUzOxdI2nM8TTi8', '纤画-刘卓(漆艺)', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTL7v0htO417V1dhaNbhwp0uZBIsicwAKhhXJvs0icEjIUCYTsdvb4nibicOiam6ebicdDOwCu8EwCYemJTA/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '遇到蟹子，鱼座惟一要做的就是把蟹子心底想的摸个透彻，因为蟹子很需要别人了解和谅解，而鱼座正好具有这种同情心旺盛的特质，加上似水的柔情，不用一时三刻，蟹子不上钩都难。不过也是因为这两个星座天生有互相吸引的特质，所以实在是不用动太多的脑筋。', '一个这么深情的组合还有什么好比的?不过若是在爱情的依赖度上，鱼鱼对巨蟹座有着莫名的依赖感，可能是因为巨蟹具有母亲本能吧!这种包容和体贴，是鱼座软弱时最好的避风港，鱼鱼其实是很脆弱的，受不得大风大浪，蟹子的两个大螯正好提供了他们最好的保证。', '99', '0', '0');
INSERT INTO `mn_xz_recard` VALUES ('399', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5KRx99bXjI9OUwz2imJiiH8', 'Mr.Y', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJYBR5t11Zx178eXIRAx4ATz9dWQ1AyapBLt6fHOZHYibpjTD7zhpJksibVf3vgJiaoDjOY9votO2sQg/132', '双子', '../images/staricon/shuangzi.png', '遇到双子座，鱼座一定要收起自己迷惘、混乱、困顿的一面，赶快摆出精明、美丽、充满神秘感的一面，保证双子座立刻就会自投罗网，对你的好奇心足以填平台湾海峡!当然，要记住，若即若离是很重要的，尤其不要让双子们看出你到底在想些什么，不然就前功尽弃了。', '这是一个怎么着都无法看好的组合，双子和双鱼座的那种相似又严重独立，让他们相处起来是鸡同鸭讲，到最后连他们自己都不了解为什么会在一起。是不太可能速配的星座组合。', '34', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('400', 'oV9VJ5KRx99bXjI9OUwz2imJiiH8', 'Mr.Y', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJYBR5t11Zx178eXIRAx4ATz9dWQ1AyapBLt6fHOZHYibpjTD7zhpJksibVf3vgJiaoDjOY9votO2sQg/132', '双子', '../images/staricon/shuangzi.png', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', '这也是一个没什么交集的组合，两人对事情的看法反应不一样，连在乎的事情也不相同，在一起的机会是很小的，但如果有机会在一起，是很需要互相了解的，不然实在很难深入交往。', '其实双鱼座不太需要被了解，因为连他们自己也不知道自己在想什么，双子只要把他们的注意力从恍惚中拉回来就算是很厉害了，再来就细心一点，看看双鱼座喜欢或需要什么，让双鱼感动一番，刻不容缓的是有空要陪着他们聊聊心情和想法，不用很久就能得到双鱼座的依赖和倾心啦!', '34', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('401', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5GvlMa4Fqos4ppJGdAK0mOE', '晴川', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKDMz5BSaVhSvrZXpJD4O3kBeuibyD76LGiautMe4sZUyQJVeyFKxGphbPaficiaWflcD65oHyKrk4fkw/132', '白羊', '../images/staricon/baiyang.png', '遇到羊羊这号人物，鱼座一定要拿出自己最温柔的一面，最好再加上楚楚可怜、无依无靠的气质，保证对羊羊手到擒来，所有的担心都可以省下来了!也许你会觉得奇怪，羊座这么好拐?对，没错。当然拐到手以后，在两个人的相处上你的温柔却是让羊羊离开你的原因。', '怎么看这两个星座麻烦的程度都是半斤八两，比不出什么结果来。不过说起来羊座还是比较主动积极一些，光这点就比犹豫度日的鱼座来得有力。而且当两人相处的时候，羊羊的热情会让鱼座感动得什么都忘了，非常甘愿地跟着羊羊的脚步前进。', '29', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('402', 'oV9VJ5GvlMa4Fqos4ppJGdAK0mOE', '晴川', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKDMz5BSaVhSvrZXpJD4O3kBeuibyD76LGiautMe4sZUyQJVeyFKxGphbPaficiaWflcD65oHyKrk4fkw/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', '这个配对在情感上有互相依赖的倾向，彼此在一起的时候感觉比较像跟家人相处，不过因为他们的个性都很浪漫，所以在别人看来，他们互相照顾依偎的相处方式让人觉得很温暖。', '羊羊对鱼鱼不用太了解，因为鱼座需要的不是别人对他的了解，而是类似同情的谅解!毕竟要真正了解他们太难了，但是白羊那种天真快乐的气质会吸引鱼座的接近，保持你的简单和直接，用来对付鱼鱼的犹豫不安，加上偶尔的温柔体贴，就足以让双鱼座充满安全感啦! ', '29', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('403', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5FUZnO4qQb-e-mTdGrrAfmE', '纤画漆艺@晓明', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83erdO8sSFKPbHZ6Vhz1hEKtduI1mofovLHTnJsyKCPiaFmYEhIQhJQGI4LMOectvcR6meZQ2ISq3ibng/132', '天秤', '../images/staricon/tianping.png', '这个组合并不是很合适的，尤其在两人的个性与对生活的看法上，都相差得非常远，刚开始可能还不觉得，但是到后来越亲密就越不能接受对方的看法，通常是很难长久经营下去的。', '其实和秤子熟识之后蟹子会蛮受不了秤子的个性的，因为秤子不喜欢负责任，也太自我，让蟹子有时恨得咬牙切齿。不过要对付秤子蟹子只要记住几个原则就可以了，首先要让秤子觉得轻松愉快，同时和他一起做一些突发奇想的事，不用很久，秤子自然就会对你另眼相看了。', '45', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('404', 'oV9VJ5FUZnO4qQb-e-mTdGrrAfmE', '纤画漆艺@晓明', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83erdO8sSFKPbHZ6Vhz1hEKtduI1mofovLHTnJsyKCPiaFmYEhIQhJQGI4LMOectvcR6meZQ2ISq3ibng/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '这是一个不太有希望的组合，因为他们不仅个性不同，而且都很受不了对方，当然这个组合两个人都各有各的特别之处，但他们偏偏就意见不合，虽然不会打起来，但也足够了! ', '蟹子最怕的事就是没有安全感了!所以秤子也不用想要抓住你，不过不管你觉得蟹子担心的事多么没道理，还是要安慰他们，就算天塌下来也不会压到他们，保证他就会乖乖的。这种话最好以每天三次的频率宣读，让蟹子没有情绪化的机会，日子就很好过了。', '45', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('405', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5PragETrsj_87mju-1Gc_UA', '网站建设、SEO优化、小程序田亮', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTIADpDHGLNh1ForAGt6vy6IgNfotQ8c96iaCoQic7mGibXekMPFMZm9U5ZwoqzOR4lMXzX1tLOwDyslg/132', '双鱼', '../images/staricon/shuangyu.png', '同星座嘛没什么可以比较的，不过鱼鱼分两种，一种是现实派，但心里有着对于理想的浪漫想法；另一种是梦幻派，天天做梦不切实际，通常会和现实社会脱节!没办法，鱼鱼是天生的极端，无法把两样事调配好，但是一遇到爱情，所有的鱼都一样有着无可救药的浪漫情怀。', '当鱼遇到鱼的时候最重要的事是了解自己在想什么，了解了自己就是了解对方啦!不过你们的问题是一样的，虽然无法替对方解答倒可以同病相怜一番，也可能因为这种情怀而让鱼鱼觉得爱上了对方，进而牺牲奉献地想尽一己之力，但过多的同情对鱼来说只会更沉沦罢了。', '89', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('406', 'oV9VJ5PragETrsj_87mju-1Gc_UA', '网站建设、SEO优化、小程序田亮', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTIADpDHGLNh1ForAGt6vy6IgNfotQ8c96iaCoQic7mGibXekMPFMZm9U5ZwoqzOR4lMXzX1tLOwDyslg/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', '同星座嘛没什么可以比较的，不过鱼鱼分两种，一种是现实派，但心里有着对于理想的浪漫想法；另一种是梦幻派，天天做梦不切实际，通常会和现实社会脱节!没办法，鱼鱼是天生的极端，无法把两样事调配好，但是一遇到爱情，所有的鱼都一样有着无可救药的浪漫情怀。', '当鱼遇到鱼的时候最重要的事是了解自己在想什么，了解了自己就是了解对方啦!不过你们的问题是一样的，虽然无法替对方解答倒可以同病相怜一番，也可能因为这种情怀而让鱼鱼觉得爱上了对方，进而牺牲奉献地想尽一己之力，但过多的同情对鱼来说只会更沉沦罢了。', '89', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('407', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5Oe41ZznHzY1xHetRBU2FTQ', '泽瑞克', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJ7Z1xTtBDIFEiaH9unZF05jtuEfA1JDT6dqI90yzWSRkIGArObgTYtAIuDNVTF7scibav8OwjYGrjA/132', '双鱼', '../images/staricon/shuangyu.png', '这可是一个超级浪漫的组合，他们不切实际的程度足以媲美\"灰姑娘\"的际遇，这个配对真的很容易变得不切实际，有可能有了爱情就不要面包，不过却是非常非常的惟美浪漫哦! ', '蟹子的全心付出会让鱼座非常感动，觉得无以为报，所以蟹子要做的事就是不断付出自己丰沛的爱心，让鱼座溺死在里面，连翻身的机会都没有，加上鱼座对巨蟹日渐变得依赖，这样一来鱼鱼就再也跑不掉了，而这个美妙的爱情配对，自然就会有幸福美满的结局出现了! ', '88', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('408', 'oV9VJ5Oe41ZznHzY1xHetRBU2FTQ', '泽瑞克', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJ7Z1xTtBDIFEiaH9unZF05jtuEfA1JDT6dqI90yzWSRkIGArObgTYtAIuDNVTF7scibav8OwjYGrjA/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '遇到蟹子，鱼座惟一要做的就是把蟹子心底想的摸个透彻，因为蟹子很需要别人了解和谅解，而鱼座正好具有这种同情心旺盛的特质，加上似水的柔情，不用一时三刻，蟹子不上钩都难。不过也是因为这两个星座天生有互相吸引的特质，所以实在是不用动太多的脑筋。', '一个这么深情的组合还有什么好比的?不过若是在爱情的依赖度上，鱼鱼对巨蟹座有着莫名的依赖感，可能是因为巨蟹具有母亲本能吧!这种包容和体贴，是鱼座软弱时最好的避风港，鱼鱼其实是很脆弱的，受不得大风大浪，蟹子的两个大螯正好提供了他们最好的保证。', '88', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('409', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5HnEZho2CLe3r2BJgcvhW90', '杨阳', 'https://wx.qlogo.cn/mmopen/vi_32/yaA4sCicvRtA9X8vNCvBr7ibA15icUBY4g72pFYxRF2Z03O0XQG3rTkI99GVQwiaHfIrDDZ7oIm4qVFdr9Vbx9VHpQ/132', '水瓶', '../images/staricon/shuiping.png', '这也是一个神秘引力配对，两个人之间虽然有着完全迥异的个性，却能摩擦出热情的火花。当然，这种配对将不被认识他们的人看好，因为差太多了，但是却常常跌破所有人的眼镜。', '这个配对中瓶子常常是被动的，因为他们很难想像对方会看上自己，不过一旦双方确定彼此都有意思之后，瓶子就会以主导者的姿态进入两个人的关系，尤其是瓶子本身具备的所有特质巨蟹们都很欣赏，而就算是缺点蟹座也会包容，所以瓶瓶的确占了很大的优势呢! ', '39', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('410', 'oV9VJ5HnEZho2CLe3r2BJgcvhW90', '杨阳', 'https://wx.qlogo.cn/mmopen/vi_32/yaA4sCicvRtA9X8vNCvBr7ibA15icUBY4g72pFYxRF2Z03O0XQG3rTkI99GVQwiaHfIrDDZ7oIm4qVFdr9Vbx9VHpQ/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '这是一个有吸引力的组合!虽然两个星座看来差十万八千里，但也是因差异反而产生更大的吸引力。当然这个配对是不容易一路顺畅到底的，能不能长久经营就要看有没有缘分。', '瓶子遇到巨蟹座只要摆出一副没什么大不了的模样就足以让蟹子佩服不已，同时在蟹子心情不好的时候讲些简单开导他们的话即可。可能就是因为瓶子座总是那么温温和和地说话，让蟹子充满安全感，所以才会冒出火花来的，所以瓶子一定要保持良好的脾气来面对蟹子才好。', '39', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('411', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5AQAv5tIeeAAKd3OOTsI7zM', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eouGFWuM06WicBhj3fQnexNgGYX3BjBk7ncxibdAia1ibBibTib3RSND8ruOFicu6Mcm6PWauIDaywDDXpFg/132', '双子', '../images/staricon/shuangzi.png', '这是一个差很多的组合。不过因为这两个星座还是算有缘分，所以这种配对很容易出现。他们虽然不算非常合适，但是因为两人都很单纯，所以只要能和平相处就会很顺利的。', '其实这两个星座真是没什么好比的，如果能够顺利在一起的话，两个人都会抱着单纯的交往心态，不会想太多，不过一般说来，蟹子想得要比双子多一些，同时蟹座也比较了解双子座的个性，能够给他很大的自由空间，而双子也需要蟹座的关怀，心理上也较依赖巨蟹哦!', '67', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('412', 'oV9VJ5AQAv5tIeeAAKd3OOTsI7zM', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eouGFWuM06WicBhj3fQnexNgGYX3BjBk7ncxibdAia1ibBibTib3RSND8ruOFicu6Mcm6PWauIDaywDDXpFg/132', '双子', '../images/staricon/shuangzi.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '这是一个性格相差很多的星座，不过如果能组合在一起缘分还蛮深的，虽然很不相同但是却能够互补，两个都不是太复杂的星座，所以不会太难了解，反而可以不太计较和平相处下去。', '说真的，遇到蟹座双子可能就辛苦一点啦!因为双子实在一点也不了解巨蟹，只知道蟹子老是在忧郁，不知道到底在忧郁什么，其实你只要常常深情款款地和蟹子表白就好，这一点应该不算太为难双子们吧!毕竟双子们应该不是会吝啬说好话的人，那就多说一点准没错啦! ', '67', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('413', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5PYYPeNYYvSfNrScK_kQqmQ', 'GZH', 'https://wx.qlogo.cn/mmopen/vi_32/62cLVFreHtLicibmOWan56HC2hMRibexyBOkFIFzEyx1mx79iack05WWmulZhJxmsHs1eXKLBYQibC14ETHUoPctZbA/132', '白羊', '../images/staricon/baiyang.png', '这个配对基本来说真的不太合，因为个性上的差异，导致两人相处起来常常处于水深火热当中，没有翻脸就不错了，其他什么沟通之类的根本就谈不上，而且往往会演变成互相指责。', '虽然两个人不太合适，但是蟹座的母性光辉还是让羊羊这个长不大的星座颇为眷恋，常常被蟹子哄得相当听话，是很占优势的一方!一开始或许没有这么严重，但是越到后来总是越演越烈，不管再多的眷恋也吵到烟消云散了，必须仔细思考两个人到底合不合适这个问题。', '59', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('414', 'oV9VJ5PYYPeNYYvSfNrScK_kQqmQ', 'GZH', 'https://wx.qlogo.cn/mmopen/vi_32/62cLVFreHtLicibmOWan56HC2hMRibexyBOkFIFzEyx1mx79iack05WWmulZhJxmsHs1eXKLBYQibC14ETHUoPctZbA/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '这个配对基本来说真的不太合，因为个性上的差异导致两人相处起来常常处于水深火热当中，没有翻脸就不错了，其他什么沟通之类的根本就谈不上，而且往往会演变成互相指责。', '羊羊对蟹座是有些依赖的，请尽量表现出你的眷恋之情，越夸张越好，老实说，蟹子就喜欢这种调调，但是羊羊心里可要拿捏好情况，因为蟹子毕竟也有他软弱的一面，必要时，白羊必须表现出他保护弱者的强势。互相理解是你们两个人相处的法宝，可不要忘了。', '59', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('415', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5AyxBb2Q-wKrTdJCvPQqGu0', '时碰', 'https://wx.qlogo.cn/mmopen/vi_32/pdXnxywfWQxnPyr4PWEvpSM491cToFphF7ylRqFP9Ria3lYbMu3coK2SlEuDCHicI9QM8EO8Tq2z7k5IfgOehxxQ/132', '摩羯', '../images/staricon/mojie.png', '这是个很缠的组合，如果他们真的有幸成为组合的话，那么精彩两字绝对不够形容!这两个星座的个性互相抵触，不过因为一样的粗心大意，所以要不就互相不满，要不就不相往来。', '鱼鱼在这段感情中比较吃香，因为摩羯们的心事鱼座一向很了解，柔情似水加上善解人意让摩羯心甘情愿兼死心踏地，早早就会降服在鱼座的脚下。不过鱼鱼的心事可不是那么简单就能被人掌握的，所以鱼鱼看来反而显得比较犹豫不决，没有摩羯们对这段感情来得坚定。', '65', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('416', 'oV9VJ5AyxBb2Q-wKrTdJCvPQqGu0', '时碰', 'https://wx.qlogo.cn/mmopen/vi_32/pdXnxywfWQxnPyr4PWEvpSM491cToFphF7ylRqFP9Ria3lYbMu3coK2SlEuDCHicI9QM8EO8Tq2z7k5IfgOehxxQ/132', '摩羯', '../images/staricon/mojie.png', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', '这是两个心情细腻的星座所组成的，虽然俩人的目标、想法都不一样，但是却有非常好的默契，对对方有深刻的了解。这个组合其实很浪漫，充满一种温馨宁静的气氛，让人羡慕。', '这个组合鱼鱼比摩羯座略占了点优势，因为摩羯座总是无法对鱼座温柔脆弱的一面视而不见，只要鱼鱼一出现摩羯就会显出强烈的保护欲，加上鱼鱼的温柔体贴总是会让摩羯们感动不已，很容易就被鱼座收买了，其他什么有的没的都会忘到天边去，再也精明不起来啦! ', '65', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('417', 'oV9VJ5Cb8EtDCD72o5FcPEQrauOw', '空', 'https://wx.qlogo.cn/mmopen/vi_32/MR5moxclpeDeJQSRtD2VHUicZz5iaN9hFb6Fny9SfWedDHkJ7wWCOPliaJNE93lopu7lXsftaga2ct0uvFyboVP0g/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '这是一个非常火辣的配对，因为双方都拥有燃烧的热情，所以对别人来说这是热烈得让人受不了的一对，因为两人对事情有非常雷同的看法，所以算是相当速配的一个组合了!', '狮子怎么说都比羊羊来得冷静一些，加上他们天生的群众魅力，羊羊可是崇拜得很哪!当然两个人在一起时狮子发号施令的可能性是比较大的，羊羊基于对英雄的尊敬，通常是照听不误的，加上两个人的想法和个性都很合，所以通常都能够顺顺利利地这么发展下去。', '82', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('418', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5Cb8EtDCD72o5FcPEQrauOw', '空', 'https://wx.qlogo.cn/mmopen/vi_32/MR5moxclpeDeJQSRtD2VHUicZz5iaN9hFb6Fny9SfWedDHkJ7wWCOPliaJNE93lopu7lXsftaga2ct0uvFyboVP0g/132', '狮子', '../images/staricon/shizi.png', '一样是热情如火的火象星座，真是没有什么好挑的了，尤其在面对别人的时候，他们的配合度绝对是一等一，叫人不羡慕嫉妒都难。是个恋情轰轰烈烈的组合，一般而言还算完美。', '羊羊对狮座是有些崇拜的，请尽量表现出你的崇拜之情，越夸张越好，老实说，狮子就喜欢这种调调，但是羊羊心里可要拿捏好情况，在该给他留面子时放任他，等到私底下再来好好“沟通”一下，相信狮子很快就会懂了，互相尊重是你们两个人相处的法宝，可不要忘了。', '82', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('419', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5B52VJWsc-Gm00bf50kawKA', '花胖虎', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq7zTLUZGsU7P5RsH7gHia6ZzCibr8RxZaz1iaFic6rb4VmFicHQPvDlexmXwTIJV1O00CmGWgC6lePTMQ/132', '天秤', '../images/staricon/tianping.png', '这是一个很能互相了解的组合，两个星座都很执著于完美，所以很了解对方内心的矛盾。这个组合适合从朋友开始发展，虽然没什么热烈的感觉，但是却很温暖，充满相知相惜的感觉。', '这个配对中蝎子很明显地比秤子强很多，因为天蝎是不太管别人死活的，但是秤子很在乎别人的感受，这样下来，当然是秤子来迁就蝎子了!而且蝎子神秘的特质正好是秤子最喜欢的，天蝎不断地引起秤子强烈的好奇心，让两人的关系老是一面倒，而且是由蝎子主控着。', '40', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('420', 'oV9VJ5B52VJWsc-Gm00bf50kawKA', '花胖虎', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq7zTLUZGsU7P5RsH7gHia6ZzCibr8RxZaz1iaFic6rb4VmFicHQPvDlexmXwTIJV1O00CmGWgC6lePTMQ/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', '这个星座组合其实还蛮合适的，两者虽然完全不同，但是看起来又很相配，都是很有魅力的星座，而且这两个星座的个性正好互补得恰到好处，如果有缘分就会是速配成功的组合!', '如果想吃定蝎座，那秤子可就不能懒散地过日子了，当然，你可不能被他看出你在备战。蝎子看起来冷静，其实是座火山，如果你气氛拿捏适当，那么成功一定是指日可待的。不要主动质问蝎座，自己小心地观察，你就会看得出来他们到底有没有上钩。', '40', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('421', 'oV9VJ5B52VJWsc-Gm00bf50kawKA', '花胖虎', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq7zTLUZGsU7P5RsH7gHia6ZzCibr8RxZaz1iaFic6rb4VmFicHQPvDlexmXwTIJV1O00CmGWgC6lePTMQ/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5J9ApOtd_lICxT2LUiw0peM', 'Leah', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83erTKV6yv5vPaOr5YwdWCMIEn17DEiauxVbbicBAbjTXpeTm7OViaRphdOC5u1wOKfSATEf5wwko5msvQ/132', '狮子', '../images/staricon/shizi.png', '这是一个颇能互相惺惺相惜的配对，尤其双方都很了解欣赏对方，因此通常会由朋友做起，如果有机会的话再凑成一对，不会太过激情，但因为有友谊的基础，让两个人可以长久相处。', '要把狮子解决掉实在是很简单的事，秤子不是不会说好听话，而是不屑说，除此什么样的事他们都做得出来。所以只要秤子和狮子搅在了一起，秤子就可以使使小计谋来吃定狮子。', '62', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('422', 'oV9VJ5J9ApOtd_lICxT2LUiw0peM', 'Leah', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83erTKV6yv5vPaOr5YwdWCMIEn17DEiauxVbbicBAbjTXpeTm7OViaRphdOC5u1wOKfSATEf5wwko5msvQ/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5B52VJWsc-Gm00bf50kawKA', '花胖虎', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq7zTLUZGsU7P5RsH7gHia6ZzCibr8RxZaz1iaFic6rb4VmFicHQPvDlexmXwTIJV1O00CmGWgC6lePTMQ/132', '天秤', '../images/staricon/tianping.png', '这个组合站出去真是非常光鲜亮丽，叫人口水流满地，光是外型气势就是人群的焦点了，加上他们举手投足配合无间的表现，提高了速配成功的可能性，更是互相了解的知心组合哦! ', '秤子特别喜欢有特色的人，而狮子可以算是很有特色了吧!就算每只狮子看起来都一样，也比一般人来得亮眼多了，所以光是外形这一点，狮子就足以赢得秤子的青睐有加。加上秤子一向是没什么决断力的，而狮子刚好是很有决断力的星座，所以正好合了秤子的胃口! ', '62', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('423', 'oV9VJ5B52VJWsc-Gm00bf50kawKA', '花胖虎', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq7zTLUZGsU7P5RsH7gHia6ZzCibr8RxZaz1iaFic6rb4VmFicHQPvDlexmXwTIJV1O00CmGWgC6lePTMQ/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5KYD2qedm-7ofyKX_jzTxQU', 'Rachel', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJ4RvVWLb9ibNtnbnCHvXGx9iclYmxylXXXIzlKsPDBM5nYibNibibTZxVMVYzRBM5pkhwqKbyf2Ow1uqw/132', '双子', '../images/staricon/shuangzi.png', '这是一个非常顺利的组合，尤其是两个人的相处是轻松又愉快。这两个星座都是轻松的，觉得就算是天塌下来也轮不到他们来顶，这么志同道合，要说不速配成功都很困难了! ', '遇到双子，秤子也不用太努力啦!只要能够让双子聊个够、表演个够，他们就会觉得和你在一起很快乐，平常也不太需要去管他们，反正该出现的时候双子们也就会出现。\"放任\"是双子们最无法招架的绝招，试问哪个小孩不喜欢没人管的好日子?而且玩够了还是会回来的。', '82', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('424', 'oV9VJ5KYD2qedm-7ofyKX_jzTxQU', 'Rachel', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJ4RvVWLb9ibNtnbnCHvXGx9iclYmxylXXXIzlKsPDBM5nYibNibibTZxVMVYzRBM5pkhwqKbyf2Ow1uqw/132', '双子', '../images/staricon/shuangzi.png', 'oV9VJ5B52VJWsc-Gm00bf50kawKA', '花胖虎', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq7zTLUZGsU7P5RsH7gHia6ZzCibr8RxZaz1iaFic6rb4VmFicHQPvDlexmXwTIJV1O00CmGWgC6lePTMQ/132', '天秤', '../images/staricon/tianping.png', '这是一个相当顺利的组合哦!因为这两个星座不但个性相似，连相处的方法也很协调，只要在一起通常就能顺利地进行下去，加上同是爱好和平的风象星座，大概连架也吵不起来吧!', '其实没有差太多啦!只不过一向在唇枪舌剑中生存的双子，遇到了天性优雅和平的天秤，那真的是觉得相当新鲜了，而且秤子不会跟双子们斗嘴，因此能相处得非常愉快，甚至无话不谈，两人相处的气氛是既和平又融洽，如此一来，叫双子不喜欢秤子还真是有点难呢!', '82', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('425', 'oV9VJ5B52VJWsc-Gm00bf50kawKA', '花胖虎', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq7zTLUZGsU7P5RsH7gHia6ZzCibr8RxZaz1iaFic6rb4VmFicHQPvDlexmXwTIJV1O00CmGWgC6lePTMQ/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5Je_Ua4sLOkcfp1e2FcOj18', '黄康', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq6iax8UGbjick5ohc6WC7Jp1YTdZVNu84nI1ChU4q5GlMOia5EL9Sia61OR0KGiaWBdT7Zzr3IibBicRopw/132', '水瓶', '../images/staricon/shuiping.png', '这可是有名的才子佳人配对哦!是人人称羡的组合，因为两个人都是风象星座的，所以不论哪一方面都是配合无间的，尤其在人际关系上双方都很受欢迎，当然让人羡慕又嫉妒了!', '这个配对中瓶子还是比较占上风的，尤其在事情的决定上完全以瓶子为主，秤子完全沾不上边!而且瓶子独特的个人风格让秤子非常迷恋，加上他们冷静理智的个性，秤子连毛病也挑不出来，完全让人吃得死死的。如果瓶子再专情一点，那天秤大概没有翻身的机会了。', '80', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('426', 'oV9VJ5Je_Ua4sLOkcfp1e2FcOj18', '黄康', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq6iax8UGbjick5ohc6WC7Jp1YTdZVNu84nI1ChU4q5GlMOia5EL9Sia61OR0KGiaWBdT7Zzr3IibBicRopw/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5B52VJWsc-Gm00bf50kawKA', '花胖虎', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq7zTLUZGsU7P5RsH7gHia6ZzCibr8RxZaz1iaFic6rb4VmFicHQPvDlexmXwTIJV1O00CmGWgC6lePTMQ/132', '天秤', '../images/staricon/tianping.png', '这是一个让人羡慕的配对哦!不管是才子佳人或才女俊男的配对都有可能出现在这个配对之中。这两个星座的个性相当相近，所以没有什么相处上的问题，只不过好像热情少了一些。', '遇到秤子时瓶子只要好好地摆出酷帅的POSE，并且说出自己独特的主张就足以让秤子注意到你的存在了。这真是一件容易的差事，事实上瓶子什么都不做的时候就像是一座冰山了，一旦他们做了什么也是我行我素而已，偏偏这些就是秤子所喜欢的类型，让秤子无法自拔。', '80', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('427', 'oV9VJ5B52VJWsc-Gm00bf50kawKA', '花胖虎', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq7zTLUZGsU7P5RsH7gHia6ZzCibr8RxZaz1iaFic6rb4VmFicHQPvDlexmXwTIJV1O00CmGWgC6lePTMQ/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5I0jW4EZ6CjB1a8CnVLPAPc', '赵夏斌', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83epUJzAtDlYYrVib8mqYzZoT1ibzGNBt3cP1eC7NH0cJJ8bRQeJJl9Q4Adglq3Y6nJRmqfMBEYF6nknA/132', '双鱼', '../images/staricon/shuangyu.png', '这可是一个非常浪漫的组合哦!想想看，两个星座一样懒也一样浪漫，个性上也都会迁就对方，是最适合谈一场如小说情节般的爱情的配对，不过这个配对有点不切实际，要长久就很难了。', '面对鱼座这种捉摸不定的性情，秤子会感到乱无头绪，因为鱼座的话语是很难了解的，而秤子们老是被气氛和鱼鱼的气质给唬住，懵懵懂懂地就谈起恋爱来。秤子一定要保持清醒，不要只看到风花雪月，只要越清楚就越能掌握全局，在感情的发展上才能慢慢地参与和主导。', '48', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('428', 'oV9VJ5I0jW4EZ6CjB1a8CnVLPAPc', '赵夏斌', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83epUJzAtDlYYrVib8mqYzZoT1ibzGNBt3cP1eC7NH0cJJ8bRQeJJl9Q4Adglq3Y6nJRmqfMBEYF6nknA/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5B52VJWsc-Gm00bf50kawKA', '花胖虎', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq7zTLUZGsU7P5RsH7gHia6ZzCibr8RxZaz1iaFic6rb4VmFicHQPvDlexmXwTIJV1O00CmGWgC6lePTMQ/132', '天秤', '../images/staricon/tianping.png', '遇到秤子你一定要比他们有个性、有主见，随性的浪漫更足以让秤子们倾倒。所以鱼座通常不用去赶流行的列车，只要能够自成一套就足以让秤子们心向往之!所以尽管搬出你的随性和自然，甚至于不食人间烟火、任性都可以，只要表现得恰到好处就足以让秤子倾心。', '这两个星座实在很相似，让两人之间产生强大的吸引力，通常他们有一样的兴趣，容易因兴趣而结缘，会欣赏彼此的个性，相处起来更是有如少女漫画一般，充满了浪漫轻松的气氛。', '48', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('429', 'oV9VJ5Je_Ua4sLOkcfp1e2FcOj18', '黄康', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq6iax8UGbjick5ohc6WC7Jp1YTdZVNu84nI1ChU4q5GlMOia5EL9Sia61OR0KGiaWBdT7Zzr3IibBicRopw/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5Bl9dgTAy8vlFXgg8v8q4pA', '天冷了多吃肉', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJZMZqg72MrznobpaUchVdgcYabSqTZnZqsVepY3a98mYCx8oraNBtp1dL7jJpZ1jOej7BRziaOu6A/132', '双子', '../images/staricon/shuangzi.png', '这个配对可是有名的黄金配!这是两个很相似的星座，在一起不用说太多话就心有灵犀一点通，默契好得让旁人妒嫉，不过这个配对没有什么热情，相处比较像朋友一般不粘腻。', '这么合的两个星座实在没什么好比的，不过在种种智力的比较中，瓶子似乎略胜一筹哦!倒也不是说双子比较笨，而是瓶子想得总是比较周全一些，双子们玩完就忘记了，而瓶子记忆力比较好，一旦翻起旧账来当然比较吃香一些，不过这两个星座都是不太会记恨就是了。', '90', '0', '0');
INSERT INTO `mn_xz_recard` VALUES ('430', 'oV9VJ5Bl9dgTAy8vlFXgg8v8q4pA', '天冷了多吃肉', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJZMZqg72MrznobpaUchVdgcYabSqTZnZqsVepY3a98mYCx8oraNBtp1dL7jJpZ1jOej7BRziaOu6A/132', '双子', '../images/staricon/shuangzi.png', 'oV9VJ5Je_Ua4sLOkcfp1e2FcOj18', '黄康', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq6iax8UGbjick5ohc6WC7Jp1YTdZVNu84nI1ChU4q5GlMOia5EL9Sia61OR0KGiaWBdT7Zzr3IibBicRopw/132', '水瓶', '../images/staricon/shuiping.png', '这是一个非常顺利的组合，一旦在一起也就没有什么理由好分开的。这是两个很像的星座，所以他们能够配合得天衣无缝，加上风象的自由倾向，让两人不但是情人更是知心的好友。', '在这两个很像的星座中，瓶子算是比较总代表也比较有定力的，所以双子常常会跟随着瓶子归纳出来的方向进行，可以说瓶瓶常常是主导的那一方，这样听起来双子好像有点吃亏，其实也是因为双子自己懒得下决定，而且两人反正已经这么像了，决定通常也差不到哪里去。', '90', '0', '0');
INSERT INTO `mn_xz_recard` VALUES ('431', 'oV9VJ5Je_Ua4sLOkcfp1e2FcOj18', '黄康', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq6iax8UGbjick5ohc6WC7Jp1YTdZVNu84nI1ChU4q5GlMOia5EL9Sia61OR0KGiaWBdT7Zzr3IibBicRopw/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5H21AHMm8hOFCSqtEh3fly4', '云琪Ada', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTL0vn8eTNt7sZEUhg0LB6crXatTdTMLkPiaiad8icLS9m2Nd1Sgicj8OGfGpae5771PgGZG1If5eGuMMw/132', '狮子', '../images/staricon/shizi.png', '这是一个引力超强的组合，会让身旁的人都感受到他们的吸引力，让人很羡慕呢!这个组合是属于互补型的，所以一旦能够速配成功就能长久地经营，不太会因为一点小事就分开。', '狮子和瓶子到底谁强谁弱呢?说真的，不相上下。不过怎么说瓶子也比狮座冷静，所以就算有什么事情狮子也绝对没有瓶子看得清楚。而偏偏瓶子又比狮座来得冷血许多，如果真的出了什么事，那么跳脚的百分之八十是狮座，瓶子只会像没事一样站在那边等结果。', '57', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('432', 'oV9VJ5H21AHMm8hOFCSqtEh3fly4', '云琪Ada', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTL0vn8eTNt7sZEUhg0LB6crXatTdTMLkPiaiad8icLS9m2Nd1Sgicj8OGfGpae5771PgGZG1If5eGuMMw/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5Je_Ua4sLOkcfp1e2FcOj18', '黄康', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq6iax8UGbjick5ohc6WC7Jp1YTdZVNu84nI1ChU4q5GlMOia5EL9Sia61OR0KGiaWBdT7Zzr3IibBicRopw/132', '水瓶', '../images/staricon/shuiping.png', '这是一个能互相吸引的星座，尤其是双方不但有完全一样的地方足以互补，也有非常相像的地方可以合作，如果能够在一起，就是让人羡慕的组合哦!这也是一个常见的配对。', '对于瓶子这种我行我素的星座，要引起他们的注意其实不难，只要和别人不一样就行了，不过要他们专情于你是不太容易的，因为他们太过好奇，所以你要永远保持新鲜感，让他们对你看不腻，那就会比较顺利了。不过最重要的是，千万不要限制瓶瓶视若生命的自由哦!', '57', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('433', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5BjTgzCqW9rV--sa6gVQccM', '六年级小咸鱼', 'https://wx.qlogo.cn/mmopen/vi_32/VFXKibq0CjWYC7xAWloksUFNm3U0I26TPRmTDMz6439XRENcWwZicNscjkgqQ3lnGt1cyB52Es29vtBKL8rwGloA/132', '天秤', '../images/staricon/tianping.png', '遇到秤子你一定要比他们有个性、有主见，随性的浪漫更足以让秤子们倾倒。所以鱼座通常不用去赶流行的列车，只要能够自成一套就足以让秤子们心向往之!所以尽管搬出你的随性和自然，甚至于不食人间烟火、任性都可以，只要表现得恰到好处就足以让秤子倾心。', '这两个星座实在很相似，让两人之间产生强大的吸引力，通常他们有一样的兴趣，容易因兴趣而结缘，会欣赏彼此的个性，相处起来更是有如少女漫画一般，充满了浪漫轻松的气氛。', '43', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('434', 'oV9VJ5BjTgzCqW9rV--sa6gVQccM', '六年级小咸鱼', 'https://wx.qlogo.cn/mmopen/vi_32/VFXKibq0CjWYC7xAWloksUFNm3U0I26TPRmTDMz6439XRENcWwZicNscjkgqQ3lnGt1cyB52Es29vtBKL8rwGloA/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5ECvEZ3xXfsoBz5i5YEfEAI', 'An d', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eryXHMwZHJrq3ib49XuUAJCW6MRrLQMGfG5DOy0VCkozjJCG12VhBNB3ibD5USGUOj6XqFX2SMM6ibjw/132', '双鱼', '../images/staricon/shuangyu.png', '这可是一个非常浪漫的组合哦!想想看，两个星座一样懒也一样浪漫，个性上也都会迁就对方，是最适合谈一场如小说情节般的爱情的配对，不过这个配对有点不切实际，要长久就很难了。', '面对鱼座这种捉摸不定的性情，秤子会感到乱无头绪，因为鱼座的话语是很难了解的，而秤子们老是被气氛和鱼鱼的气质给唬住，懵懵懂懂地就谈起恋爱来。秤子一定要保持清醒，不要只看到风花雪月，只要越清楚就越能掌握全局，在感情的发展上才能慢慢地参与和主导。', '43', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('435', 'oV9VJ5Je_Ua4sLOkcfp1e2FcOj18', '黄康', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq6iax8UGbjick5ohc6WC7Jp1YTdZVNu84nI1ChU4q5GlMOia5EL9Sia61OR0KGiaWBdT7Zzr3IibBicRopw/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5KYD2qedm-7ofyKX_jzTxQU', 'Rachel', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJ4RvVWLb9ibNtnbnCHvXGx9iclYmxylXXXIzlKsPDBM5nYibNibibTZxVMVYzRBM5pkhwqKbyf2Ow1uqw/132', '双子', '../images/staricon/shuangzi.png', '这个配对可是有名的黄金配!这是两个很相似的星座，在一起不用说太多话就心有灵犀一点通，默契好得让旁人妒嫉，不过这个配对没有什么热情，相处比较像朋友一般不粘腻。', '这么合的两个星座实在没什么好比的，不过在种种智力的比较中，瓶子似乎略胜一筹哦!倒也不是说双子比较笨，而是瓶子想得总是比较周全一些，双子们玩完就忘记了，而瓶子记忆力比较好，一旦翻起旧账来当然比较吃香一些，不过这两个星座都是不太会记恨就是了。', '89', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('436', 'oV9VJ5KYD2qedm-7ofyKX_jzTxQU', 'Rachel', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJ4RvVWLb9ibNtnbnCHvXGx9iclYmxylXXXIzlKsPDBM5nYibNibibTZxVMVYzRBM5pkhwqKbyf2Ow1uqw/132', '双子', '../images/staricon/shuangzi.png', 'oV9VJ5Je_Ua4sLOkcfp1e2FcOj18', '黄康', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq6iax8UGbjick5ohc6WC7Jp1YTdZVNu84nI1ChU4q5GlMOia5EL9Sia61OR0KGiaWBdT7Zzr3IibBicRopw/132', '水瓶', '../images/staricon/shuiping.png', '这是一个非常顺利的组合，一旦在一起也就没有什么理由好分开的。这是两个很像的星座，所以他们能够配合得天衣无缝，加上风象的自由倾向，让两人不但是情人更是知心的好友。', '在这两个很像的星座中，瓶子算是比较总代表也比较有定力的，所以双子常常会跟随着瓶子归纳出来的方向进行，可以说瓶瓶常常是主导的那一方，这样听起来双子好像有点吃亏，其实也是因为双子自己懒得下决定，而且两人反正已经这么像了，决定通常也差不到哪里去。', '89', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('437', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5G9gx5b-59uyhmR5pQvCCMA', '燕子', 'https://wx.qlogo.cn/mmopen/vi_32/ysYKKdicaqYjUib1C252IF5Il9e0Bu9ibTQiabHxTuUNvK6piaUI6LnQCV0aOTToI7Ls7HTgR9UWFRbJgf611fSWCvg/132', '水瓶', '../images/staricon/shuiping.png', '这也是一个神秘引力配对，两个人之间虽然有着完全迥异的个性，却能摩擦出热情的火花。当然，这种配对将不被认识他们的人看好，因为差太多了，但是却常常跌破所有人的眼镜。', '这个配对中瓶子常常是被动的，因为他们很难想像对方会看上自己，不过一旦双方确定彼此都有意思之后，瓶子就会以主导者的姿态进入两个人的关系，尤其是瓶子本身具备的所有特质巨蟹们都很欣赏，而就算是缺点蟹座也会包容，所以瓶瓶的确占了很大的优势呢! ', '21', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('438', 'oV9VJ5G9gx5b-59uyhmR5pQvCCMA', '燕子', 'https://wx.qlogo.cn/mmopen/vi_32/ysYKKdicaqYjUib1C252IF5Il9e0Bu9ibTQiabHxTuUNvK6piaUI6LnQCV0aOTToI7Ls7HTgR9UWFRbJgf611fSWCvg/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '这是一个有吸引力的组合!虽然两个星座看来差十万八千里，但也是因差异反而产生更大的吸引力。当然这个配对是不容易一路顺畅到底的，能不能长久经营就要看有没有缘分。', '瓶子遇到巨蟹座只要摆出一副没什么大不了的模样就足以让蟹子佩服不已，同时在蟹子心情不好的时候讲些简单开导他们的话即可。可能就是因为瓶子座总是那么温温和和地说话，让蟹子充满安全感，所以才会冒出火花来的，所以瓶子一定要保持良好的脾气来面对蟹子才好。', '21', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('439', 'oV9VJ5Je_Ua4sLOkcfp1e2FcOj18', '黄康', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq6iax8UGbjick5ohc6WC7Jp1YTdZVNu84nI1ChU4q5GlMOia5EL9Sia61OR0KGiaWBdT7Zzr3IibBicRopw/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', '这实在是个不太乐观的组合，这两个星座不管在哪一方面都非常不同，尤其不同的地方刚好犯冲，足以让大家为他们捏把汗，不过一般说来这两个星座是相当势均力敌的组合。', '瓶子遇到蝎子时，只要摆出自信和高深莫测的微笑就好，自然而然就会引起蝎子的注意力。而且瓶子比蝎座更加寒冷，这种冷法足以让蝎子们升起强烈的挑战欲，这时候瓶子只要守株待兔就好了，不用花太多力气就可以把蝎座拐到手，可以说瓶子的魅力是谁都无法挡的!', '34', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('440', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5Je_Ua4sLOkcfp1e2FcOj18', '黄康', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eq6iax8UGbjick5ohc6WC7Jp1YTdZVNu84nI1ChU4q5GlMOia5EL9Sia61OR0KGiaWBdT7Zzr3IibBicRopw/132', '水瓶', '../images/staricon/shuiping.png', '这是个不太能达成共识的组合。常常都是明争暗斗的，就算两个人表面上看起来不错，但私底下可能是天敌，战况精彩得让人目不暇接，在配对上实在算是太过凶险的配对。', '因为瓶子比蝎子来得任性一点，所以瓶子胜利的机会比较大，因为瓶子可以不买蝎座的账，但是要蝎座不管瓶子，那会让蝎子感到芒刺在背，很不舒服，光是这一点瓶子就占了上风，加上瓶子的我行我素，比起蝎子来算是没有什么目标，让蝎座完全搞不清楚瓶子到底在想什么。', '34', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('441', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5PaT5CETXIM9jDoGg-uxE4Y', '啾！', 'https://wx.qlogo.cn/mmopen/vi_32/fsx2QhMELib11Rg82a3MY9VI9CK0ibLEZZGlPgHdib4wIZWkhAnk06SB9XGZChth1DeicfudA9r4ukCmmWVnkJibyqA/132', '天蝎', '../images/staricon/tianxie.png', '这是一个很专情的组合!可以说那些故事小说中大部分的描写专情的配对就是这种组合啦!这真的是一对足以让所有人眼红的情侣，美满深情到每个人都觉得是神话，真的非常难得哦! ', '这种超完美配对根本没什么谁强谁弱的问题，如果真的要说，那么天蝎算是这个配对中比较强势的角色，所以占了比较多的优势。主要是因为比起巨蟹座，蝎子显得有主见而且有行动力多了，他一定是比较主动的那一方，加上天生的神秘魅力，真是让人不得不倾倒的角色。', '88', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('442', 'oV9VJ5PaT5CETXIM9jDoGg-uxE4Y', '啾！', 'https://wx.qlogo.cn/mmopen/vi_32/fsx2QhMELib11Rg82a3MY9VI9CK0ibLEZZGlPgHdib4wIZWkhAnk06SB9XGZChth1DeicfudA9r4ukCmmWVnkJibyqA/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '这是一个深情的组合，这两个星座对爱情的态度都是非常专一的，一旦认定对方就不会想要改变，再加上两个人都非常重视感情的长久经营，所以能稳定下来的机率也是相当高的。', '面对蟹子，天蝎真的只要表现出自己的深情款款就好，光是这一点就足以让蟹子手忙脚乱，不知如何是好，表面上虽然倔强得很，但是实际上心里早已闹翻天，除非他们心有所属，不然大部分的蟹座都会因此感动不已，还可能偷偷动了心，蝎子只要坐在一边等着胜利到来即可。', '88', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('443', 'oV9VJ5Cb8EtDCD72o5FcPEQrauOw', '空', 'https://wx.qlogo.cn/mmopen/vi_32/MR5moxclpeDeJQSRtD2VHUicZz5iaN9hFb6Fny9SfWedDHkJ7wWCOPliaJNE93lopu7lXsftaga2ct0uvFyboVP0g/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5PaT5CETXIM9jDoGg-uxE4Y', '啾！', 'https://wx.qlogo.cn/mmopen/vi_32/fsx2QhMELib11Rg82a3MY9VI9CK0ibLEZZGlPgHdib4wIZWkhAnk06SB9XGZChth1DeicfudA9r4ukCmmWVnkJibyqA/132', '天蝎', '../images/staricon/tianxie.png', '这也是一个战况颇激烈的组合，因为双方都有当\"王者\"的特质，不争得你死我活实在是太对不起自己了!这种配对的两人风格不但完全不同，就连对事情的看法也很不一样哦! ', '虽然两人可以说有些不相上下，不过若真的要论输赢的话，狮子可能略逊天蝎一筹哦!为什么呢?因为如果狮子算是来明的，那么蝎子绝对是选择耍阴的!没错，天蝎就是黑暗的帝王，不是说\"暗箭难防\"吗?所以狮子座遇到这么阴狠的天蝎座，可能就英雄无用武之地了!', '71', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('444', 'oV9VJ5PaT5CETXIM9jDoGg-uxE4Y', '啾！', 'https://wx.qlogo.cn/mmopen/vi_32/fsx2QhMELib11Rg82a3MY9VI9CK0ibLEZZGlPgHdib4wIZWkhAnk06SB9XGZChth1DeicfudA9r4ukCmmWVnkJibyqA/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5Cb8EtDCD72o5FcPEQrauOw', '空', 'https://wx.qlogo.cn/mmopen/vi_32/MR5moxclpeDeJQSRtD2VHUicZz5iaN9hFb6Fny9SfWedDHkJ7wWCOPliaJNE93lopu7lXsftaga2ct0uvFyboVP0g/132', '狮子', '../images/staricon/shizi.png', '这是一个让人非常头痛的组合，简直就验证了\"一山不容二虎\"这句话，凭着他们两个都具有天生的王者气息，就足以构成一场很不好打的仗，不成为死对头就该偷笑了! ', '要把狮子解决掉实在是很简单的事，蝎子不是不会说好听的话，而是不屑说，可是一旦蝎子要耍阴谋，那什么样的事他们都做得出来。对蝎子而言要安抚狮子实在是一件很容易的事，只是看蝎子要不要和狮子搅和而已，蝎子就会以使使小计谋来吃定狮子。', '71', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('445', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', '这是个小说般的组合，不但浪漫热情，还有点不切实际，这两个星座是大合的星座，不但对事情的看法相近，还能激起深刻的感觉，而且对感情的态度相同，一旦速配成功就很稳定。', '遇到鱼座蝎子可以不用那么激情，反而要在两个人之间培育出那种温暖隽永的感觉，才能让感情丰富的双鱼座觉得特别的眷恋，也才能留住鱼鱼的心，所以蝎子平常的酷样就可以收起来了，赶快摆出温柔多情的样子吧!这样才会让双鱼因为你的温柔而更加地深溺下去哦! ', '81', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('446', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', '两人在一起时，双鱼的浪漫多情会让蝎子不可自拔，加上双鱼对蝎子的了解相当深刻，叫蝎子不能太认真都难。当然蝎座的魅力也相当吸引鱼座，不过爱情上的浪漫与温柔都是由鱼鱼主导出来的，再加上鱼座编剧的能力无人能比，蝎子不溺毙在鱼座的柔情似水下都很难。', '遇到蝎座，鱼鱼只要充分地表现出自己温柔天真的模样，保证蝎子很快被你的天真吸引，再来鱼鱼只要保持若即若离的距离，暗暗撩拨蝎座的心情就好，保证他们会自投罗网，而鱼座只需笑吟吟地在一旁喝茶等待，蝎子自然会排除万难来鱼鱼的身边赖着不走了!', '81', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('447', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5Oe41ZznHzY1xHetRBU2FTQ', '泽瑞克', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJ7Z1xTtBDIFEiaH9unZF05jtuEfA1JDT6dqI90yzWSRkIGArObgTYtAIuDNVTF7scibav8OwjYGrjA/132', '双鱼', '../images/staricon/shuangyu.png', '这个配对在情感上有互相依赖的倾向，彼此在一起的时候感觉比较像跟家人相处，不过因为他们的个性都很浪漫，所以在别人看来，他们互相照顾依偎的相处方式让人觉得很温暖。', '羊羊对鱼鱼不用太了解，因为鱼座需要的不是别人对他的了解，而是类似同情的谅解!毕竟要真正了解他们太难了，但是白羊那种天真快乐的气质会吸引鱼座的接近，保持你的简单和直接，用来对付鱼鱼的犹豫不安，加上偶尔的温柔体贴，就足以让双鱼座充满安全感啦! ', '34', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('448', 'oV9VJ5Oe41ZznHzY1xHetRBU2FTQ', '泽瑞克', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJ7Z1xTtBDIFEiaH9unZF05jtuEfA1JDT6dqI90yzWSRkIGArObgTYtAIuDNVTF7scibav8OwjYGrjA/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '遇到羊羊这号人物，鱼座一定要拿出自己最温柔的一面，最好再加上楚楚可怜、无依无靠的气质，保证对羊羊手到擒来，所有的担心都可以省下来了!也许你会觉得奇怪，羊座这么好拐?对，没错。当然拐到手以后，在两个人的相处上你的温柔却是让羊羊离开你的原因。', '怎么看这两个星座麻烦的程度都是半斤八两，比不出什么结果来。不过说起来羊座还是比较主动积极一些，光这点就比犹豫度日的鱼座来得有力。而且当两人相处的时候，羊羊的热情会让鱼座感动得什么都忘了，非常甘愿地跟着羊羊的脚步前进。', '34', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('449', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5KLkGjwvSkGfW7P9exg5FB4', '灰太狼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLiaicIp9Ic6BDVnC6TNdohLccT6fjQ2xXztMs6Xo8KZyyl2Zt2Mv4hpAvDsb9WxUOFdqYicicUpFEjmQ/132', '天蝎', '../images/staricon/tianxie.png', '两个星座之间充满了特殊的吸引力，能够碰在一起表示你们相当有缘分，不过两个人的基本个性仍然有很大的差异，除了莫名的力量牵引外，能不能好好相处还是得靠个人努力啦!', '天蝎的酷样会让很多人觉得他们太冷，事实上天蝎是个热情内敛的星座，羊羊的热血澎湃刚好足以激起他们的反应，和蝎子在一起时羊羊只要够“专心”就可以了，因为喜新厌旧是蝎子所不允许的，羊羊要考虑清楚再决定要不要招惹天蝎座，以免到时候搞得大家不欢而散。', '26', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('450', 'oV9VJ5KLkGjwvSkGfW7P9exg5FB4', '灰太狼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLiaicIp9Ic6BDVnC6TNdohLccT6fjQ2xXztMs6Xo8KZyyl2Zt2Mv4hpAvDsb9WxUOFdqYicicUpFEjmQ/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '这是一个充满吸引力的组合哦!虽然两个星座看起来实在没什么瓜葛，但是你们就是能够感受到对方身上特殊的魅力，而且你们谈的恋情也非常火热，算是一个颇有激情的组合呢! ', '遇到羊羊，蝎子只要摆好姿势等着羊座自动前来上钩就好了，实在不用花太多力气，羊羊虽然是人群的焦点，但总是会看到黑暗中的蝎子，并为蝎子们神魂颠倒，通常蝎子只要稍稍做出诱惑的样子，羊羊就会开开心心地与他们搅和下去，想拐羊羊，诱惑的心情准备好就行。', '26', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('451', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5ItqkObY8t1r-xR-PLZGkE8', '布衣人', 'https://wx.qlogo.cn/mmopen/vi_32/SyxJ4byZia9EtianmGTrbcbMRQrqOEdibEP31jbgrnxU49qlJVDicspVd0X4AhjLkAd31gXMURV18qsZOlAk2YSyQA/132', '金牛', '../images/staricon/jinniu.png', '这虽然不是大好或大坏的组合，但还算蛮有缘分的，因为这两个星座个性完全不一样，却能互相吸引，不过吸引归吸引，仍然要看两个人互相的配合度才能决定能不能继续下去。', '牛牛是以实际、固执著名的，所以不要和他硬碰硬，那绝对划不来，聪明的羊羊应该赶快去收集数据资料，或想好所谓的“得失利害论”说给牛牛听，相信不用太用力就能说服他了。在爱情上也要表现得条件十足、未来一片光明、充满计划，那牛牛就会觉得安全而放心。', '27', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('452', 'oV9VJ5ItqkObY8t1r-xR-PLZGkE8', '布衣人', 'https://wx.qlogo.cn/mmopen/vi_32/SyxJ4byZia9EtianmGTrbcbMRQrqOEdibEP31jbgrnxU49qlJVDicspVd0X4AhjLkAd31gXMURV18qsZOlAk2YSyQA/132', '金牛', '../images/staricon/jinniu.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '羊羊和牛牛虽然不算什么大凶组合，但是两个人的个性真是天差地别，相处时不由得让身边的人捏把冷汗!这完全是急惊风遇见慢郎中的配对，怎么样也合不来，但也可以是互补的。', '羊羊抓狂可是很吓人的，不过通常他们都没什么持久性，生完气五分钟后就忘得干干净净；牛牛可以趁这个时候，把刚才没讨论完的事说完，反正这时羊羊也懒得再说什么了，结果一切还是照着牛牛的意愿去完成。这种解决办法既高明又不用伤和气，真是再好也没有了呢! ', '27', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('453', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5KgEJMkdO-Go26cpB5DILL0', '卢建强', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2rv83LZJGcVcpwuSwh9c9qLqEic8WpUT4IiaNVsItJURtfyO9lH2KrLBT6NXhllZeY4cRib1N2yFfg/132', '巨蟹', '../images/staricon/juxie.png', '这个配对基本来说真的不太合，因为个性上的差异导致两人相处起来常常处于水深火热当中，没有翻脸就不错了，其他什么沟通之类的根本就谈不上，而且往往会演变成互相指责。', '羊羊对蟹座是有些依赖的，请尽量表现出你的眷恋之情，越夸张越好，老实说，蟹子就喜欢这种调调，但是羊羊心里可要拿捏好情况，因为蟹子毕竟也有他软弱的一面，必要时，白羊必须表现出他保护弱者的强势。互相理解是你们两个人相处的法宝，可不要忘了。', '42', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('454', 'oV9VJ5KgEJMkdO-Go26cpB5DILL0', '卢建强', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2rv83LZJGcVcpwuSwh9c9qLqEic8WpUT4IiaNVsItJURtfyO9lH2KrLBT6NXhllZeY4cRib1N2yFfg/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '这个配对基本来说真的不太合，因为个性上的差异，导致两人相处起来常常处于水深火热当中，没有翻脸就不错了，其他什么沟通之类的根本就谈不上，而且往往会演变成互相指责。', '虽然两个人不太合适，但是蟹座的母性光辉还是让羊羊这个长不大的星座颇为眷恋，常常被蟹子哄得相当听话，是很占优势的一方!一开始或许没有这么严重，但是越到后来总是越演越烈，不管再多的眷恋也吵到烟消云散了，必须仔细思考两个人到底合不合适这个问题。', '42', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('455', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5DcfjXmw9mGPSvhzINlOj68', '刘祖梅Lzm，', 'https://wx.qlogo.cn/mmopen/vi_32/c3V5vVXgibUgNQk589YXuV43lZGnXE8VRaTVnQYqqJQRQOibicPOGyEsiaxkDdPg77XQRSdvBHT7MuBGFvSCdWBmDw/132', '射手', '../images/staricon/sheshou.png', '这是一个非常好的组合，个性都同样的热情大胆，发展出来的恋情也是既热情又浪漫!感情顺利之外，两人还有类似的想法和个性，所以相处起来非常自在，是个很圆满的配对哦! ', '射手就是喜欢白羊天真直率的样子，所以羊羊不必想太多，保持自然本性就可以了，至于射手座喜爱自由的个性，羊羊也不必太介意，不然跟着他到处跑也行，反正给他足够的自由，和他在一起的时候让他觉得轻松自在，让他什么话都可以跟你聊，那就成功了一半啦!', '96', '0', '1');
INSERT INTO `mn_xz_recard` VALUES ('456', 'oV9VJ5DcfjXmw9mGPSvhzINlOj68', '刘祖梅Lzm，', 'https://wx.qlogo.cn/mmopen/vi_32/c3V5vVXgibUgNQk589YXuV43lZGnXE8VRaTVnQYqqJQRQOibicPOGyEsiaxkDdPg77XQRSdvBHT7MuBGFvSCdWBmDw/132', '射手', '../images/staricon/sheshou.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '这是一个活力四射的组合，不仅一拍即合，而且进展得很快，因为两个人都是单纯、没有心机的星座，所以相处起来非常的直接，总是火辣辣地让每个人羡慕不已，是一个超级般配组合。', '射手从来没见过像羊座这么坦白的人呢!让射手们和羊座在一起的时候觉得全身舒畅，加上羊羊与生俱来的性感魅力，叫射手们不着迷都难呢!也就是因为羊羊具有这么跌破射手们眼镜的单纯直接，总是可以在一群人当中立刻引起射手的注意，让射手们兴起征服的欲望。', '96', '0', '0');
INSERT INTO `mn_xz_recard` VALUES ('457', 'oV9VJ5Oe41ZznHzY1xHetRBU2FTQ', '泽瑞克', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJ7Z1xTtBDIFEiaH9unZF05jtuEfA1JDT6dqI90yzWSRkIGArObgTYtAIuDNVTF7scibav8OwjYGrjA/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5HnEZho2CLe3r2BJgcvhW90', '杨阳', 'https://wx.qlogo.cn/mmopen/vi_32/yaA4sCicvRtA9X8vNCvBr7ibA15icUBY4g72pFYxRF2Z03O0XQG3rTkI99GVQwiaHfIrDDZ7oIm4qVFdr9Vbx9VHpQ/132', '水瓶', '../images/staricon/shuiping.png', '遇到瓶子，鱼鱼只要了解瓶子的理想是什么，而且无条件地支持他们，就会得到瓶子的友情，有了友情再加把劲就会是爱情了!对瓶子来说爱情和友情是同一个发源地，没太多分别。而且瓶子不喜欢被限制自由，所以不论发生什么事都不要太计较，良好的沟通是重要的一步。', '水瓶在这个组合中比较吃香一点，因为瓶瓶很实在，一有理想就要进行，再不切实际也一样，而鱼鱼纯做梦的状况就比较多了，一旦在一起，瓶瓶的行动一定比鱼鱼快，鱼鱼只能乖乖听话。不过因为鱼座喜欢瓶子实行理想的样子，对瓶子羡慕不已，所以也是心甘情愿的。', '43', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('458', 'oV9VJ5HnEZho2CLe3r2BJgcvhW90', '杨阳', 'https://wx.qlogo.cn/mmopen/vi_32/yaA4sCicvRtA9X8vNCvBr7ibA15icUBY4g72pFYxRF2Z03O0XQG3rTkI99GVQwiaHfIrDDZ7oIm4qVFdr9Vbx9VHpQ/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5Oe41ZznHzY1xHetRBU2FTQ', '泽瑞克', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJ7Z1xTtBDIFEiaH9unZF05jtuEfA1JDT6dqI90yzWSRkIGArObgTYtAIuDNVTF7scibav8OwjYGrjA/132', '双鱼', '../images/staricon/shuangyu.png', '这是一个非常暧昧不明的组合，因为两人对爱情的态度不明确，所以在一起时两人的相处方式会相当匪夷所思，一点也不像一般的情侣，反而会发展出自己的另类爱情模式哦!', '和鱼座过招最重要的就是学习如何\"钓鱼\"，先把鱼座的个性摸透了，再拿出钓饵来，勾引鱼座一步一步上钩，只要瓶子还有这个耐心，通常就能享受到钓鱼的成果，而鱼座在爱情中有不少的暧昧气氛，不妨就陪他们玩一下，让浪漫的鱼子沉沦得更彻底，也会更加的不可自拔。', '43', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('459', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5K_I3vA9CaOj1ECFiyUYMN0', '张会计', 'https://wx.qlogo.cn/mmopen/vi_32/KxafqL5JxKTr6D093G9dqEicw4SC895HRLTN8cQfQibGxSicvlIBG2zDVwfEjPypBaLf58SEESNsInyiaiahIiaptK8g/132', '狮子', '../images/staricon/shizi.png', '一样是热情如火的火象星座，真是没有什么好挑的了，尤其在面对别人的时候，他们的配合度绝对是一等一，叫人不羡慕嫉妒都难。是个恋情轰轰烈烈的组合，一般而言还算完美。', '羊羊对狮座是有些崇拜的，请尽量表现出你的崇拜之情，越夸张越好，老实说，狮子就喜欢这种调调，但是羊羊心里可要拿捏好情况，在该给他留面子时放任他，等到私底下再来好好“沟通”一下，相信狮子很快就会懂了，互相尊重是你们两个人相处的法宝，可不要忘了。', '83', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('460', 'oV9VJ5K_I3vA9CaOj1ECFiyUYMN0', '张会计', 'https://wx.qlogo.cn/mmopen/vi_32/KxafqL5JxKTr6D093G9dqEicw4SC895HRLTN8cQfQibGxSicvlIBG2zDVwfEjPypBaLf58SEESNsInyiaiahIiaptK8g/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '这是一个非常火辣的配对，因为双方都拥有燃烧的热情，所以对别人来说这是热烈得让人受不了的一对，因为两人对事情有非常雷同的看法，所以算是相当速配的一个组合了!', '狮子怎么说都比羊羊来得冷静一些，加上他们天生的群众魅力，羊羊可是崇拜得很哪!当然两个人在一起时狮子发号施令的可能性是比较大的，羊羊基于对英雄的尊敬，通常是照听不误的，加上两个人的想法和个性都很合，所以通常都能够顺顺利利地这么发展下去。', '83', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('461', 'oV9VJ5HnEZho2CLe3r2BJgcvhW90', '杨阳', 'https://wx.qlogo.cn/mmopen/vi_32/yaA4sCicvRtA9X8vNCvBr7ibA15icUBY4g72pFYxRF2Z03O0XQG3rTkI99GVQwiaHfIrDDZ7oIm4qVFdr9Vbx9VHpQ/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5EcoaPX3HJQ0nQiOkyOtM-0', '舜', 'https://wx.qlogo.cn/mmopen/vi_32/kStytFZAE8qp9FIxrslpPWrYtQCJs98y2siaOnjBXrtSQMg8er5BpGpS6ITm9EnjAhx951RTg0SzEqKg87vSHHA/132', '金牛', '../images/staricon/jinniu.png', '这可是一个非常激烈的组合哦!所谓的激烈是因为两个星座是完全不同的两个星座，若不是有孽缘在身就老死不相往来的那种，所以相处起来总是刀光剑影，让人捏了好几把汗。', '这个组合瓶子会比较高手一点。其实最主要的原因是瓶子的脸皮比牛座来得厚一点，瓶子可是一点也不管别人怎么想，偏偏牛座却是死要面子，对于有损颜面的事他们说什么也做不出来，这下子瓶子自然就把牛牛吃得死死的，让牛牛在一旁真是叫天天不应，叫地地不灵。', '36', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('462', 'oV9VJ5EcoaPX3HJQ0nQiOkyOtM-0', '舜', 'https://wx.qlogo.cn/mmopen/vi_32/kStytFZAE8qp9FIxrslpPWrYtQCJs98y2siaOnjBXrtSQMg8er5BpGpS6ITm9EnjAhx951RTg0SzEqKg87vSHHA/132', '金牛', '../images/staricon/jinniu.png', 'oV9VJ5HnEZho2CLe3r2BJgcvhW90', '杨阳', 'https://wx.qlogo.cn/mmopen/vi_32/yaA4sCicvRtA9X8vNCvBr7ibA15icUBY4g72pFYxRF2Z03O0XQG3rTkI99GVQwiaHfIrDDZ7oIm4qVFdr9Vbx9VHpQ/132', '水瓶', '../images/staricon/shuiping.png', '这可是个很可能水火不容的配对，因为两个人的差异之大，简直可以从地球到月球了!所以如果有机会配对成功，会需要花许多的时间来调整两人的差异，不然实在不太乐观哦! ', '对于瓶座的我行我素兼不切实际，牛牛是完全不能接受的，既然不能接受，那么最好的方法就是实行三不管政策!再加上瓶子天生就渴望自由，不如放任他们，平常出出意见就好，让瓶瓶发现原来你是这么的理性又好沟通的(当然不一定是真的)，他们就会非常地认同你哦!', '36', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('463', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5EcoaPX3HJQ0nQiOkyOtM-0', '舜', 'https://wx.qlogo.cn/mmopen/vi_32/kStytFZAE8qp9FIxrslpPWrYtQCJs98y2siaOnjBXrtSQMg8er5BpGpS6ITm9EnjAhx951RTg0SzEqKg87vSHHA/132', '金牛', '../images/staricon/jinniu.png', '这个组合是一个稳定又实在的组合，因为这两个星座都追求稳定，重视家庭，可能在一起没多久就已经计划到成家立业了，加上两个人都很了解对方，是很速配的星座配对呢! ', '金牛座的稳定是蟹座最喜欢的，因为老是觉得不够安全的蟹座很需要别人带来的安全感，而牛座的稳定踏实加上充满自信的表现让蟹子非常安心，很希望能够永远都在这种保护之下，所以牛牛对蟹座是很有吸引力的，虽然两个星座没差很多，但牛座还是可能赢那么一点点! ', '67', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('464', 'oV9VJ5EcoaPX3HJQ0nQiOkyOtM-0', '舜', 'https://wx.qlogo.cn/mmopen/vi_32/kStytFZAE8qp9FIxrslpPWrYtQCJs98y2siaOnjBXrtSQMg8er5BpGpS6ITm9EnjAhx951RTg0SzEqKg87vSHHA/132', '金牛', '../images/staricon/jinniu.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '这是一组好朋友的组合，如果能凑在一起，会是很了解对方的一对，尤其两人都追求稳定，使得关系一旦成立就不太可能有什么大变动出现，感情不会很激烈，但可以非常的长久。', '蟹子是了解牛牛的，牛牛也蛮知道螃蟹在想什么，每当蟹子又开始胡思乱想兼歇斯底里的时候，牛牛只要摆出一副天塌下来也没关系的模样，蟹子就能得到足够的安全感而安静下来，所以蟹座蛮依赖牛座，当然牛牛也会照顾蟹子，双方其实还算势均力敌，差别并不算大。', '67', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('465', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5DAKqP-sNzbYx6Ib7_BwgTg', '肉丸', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKE75UplojzkicUv9TWNFZibM1PjkUC2kaKNaOCRcZ6Ku2qSHNDBvdy1VItgib2pI4nC8ZvDkvkFxXmQ/132', '天蝎', '../images/staricon/tianxie.png', '这是一个很专情的组合!可以说那些故事小说中大部分的描写专情的配对就是这种组合啦!这真的是一对足以让所有人眼红的情侣，美满深情到每个人都觉得是神话，真的非常难得哦! ', '这种超完美配对根本没什么谁强谁弱的问题，如果真的要说，那么天蝎算是这个配对中比较强势的角色，所以占了比较多的优势。主要是因为比起巨蟹座，蝎子显得有主见而且有行动力多了，他一定是比较主动的那一方，加上天生的神秘魅力，真是让人不得不倾倒的角色。', '88', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('466', 'oV9VJ5DAKqP-sNzbYx6Ib7_BwgTg', '肉丸', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKE75UplojzkicUv9TWNFZibM1PjkUC2kaKNaOCRcZ6Ku2qSHNDBvdy1VItgib2pI4nC8ZvDkvkFxXmQ/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '这是一个深情的组合，这两个星座对爱情的态度都是非常专一的，一旦认定对方就不会想要改变，再加上两个人都非常重视感情的长久经营，所以能稳定下来的机率也是相当高的。', '面对蟹子，天蝎真的只要表现出自己的深情款款就好，光是这一点就足以让蟹子手忙脚乱，不知如何是好，表面上虽然倔强得很，但是实际上心里早已闹翻天，除非他们心有所属，不然大部分的蟹座都会因此感动不已，还可能偷偷动了心，蝎子只要坐在一边等着胜利到来即可。', '88', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('467', 'oV9VJ5DcfjXmw9mGPSvhzINlOj68', '刘祖梅Lzm，', 'https://wx.qlogo.cn/mmopen/vi_32/c3V5vVXgibUgNQk589YXuV43lZGnXE8VRaTVnQYqqJQRQOibicPOGyEsiaxkDdPg77XQRSdvBHT7MuBGFvSCdWBmDw/132', '射手', '../images/staricon/sheshou.png', 'oV9VJ5BemKRKhr9gGLgFV864MZ8M', '何金辉', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTIGC5XjiaibUOrPDxkJLtfXtO0etpGsXceDyWFjckkbT7M8iac1AoAgskTdgVgv0vho8v8NVDyk91etw/132', '狮子', '../images/staricon/shizi.png', '这是两个火象星座的组合，当然也够火辣的了，谈起恋爱来绝对比别人来的热情高出数倍!这个组合因为个性上的相似，所以连不满的机会都没有，注定是个超级绝对的速配组合!', '想对付狮子座只要让他知道你在哪里就好了，主动报告会让他们放心，也不会多说什么，算是很好解决的。平常有事没事陪着他们一起做些琐事也会让他们觉得高兴，通常射手想做的事情也会是狮座有兴趣的，不妨凑在一起搞怪作乱，会让你们之间产生更深厚的感情哦!', '84', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('468', 'oV9VJ5BemKRKhr9gGLgFV864MZ8M', '何金辉', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTIGC5XjiaibUOrPDxkJLtfXtO0etpGsXceDyWFjckkbT7M8iac1AoAgskTdgVgv0vho8v8NVDyk91etw/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5DcfjXmw9mGPSvhzINlOj68', '刘祖梅Lzm，', 'https://wx.qlogo.cn/mmopen/vi_32/c3V5vVXgibUgNQk589YXuV43lZGnXE8VRaTVnQYqqJQRQOibicPOGyEsiaxkDdPg77XQRSdvBHT7MuBGFvSCdWBmDw/132', '射手', '../images/staricon/sheshou.png', '这也是一个绝好配对哦!两个一样爱玩又一样热情的星座遇在一起冒出的火花可是会烧死人的，加上他们对很多事情都有相同的看法，真是超级速配的组合，足以让人羡慕得流口水了!', '说真的，既然这么合，实在也就没什么好比较的了，不过如果真拿来对照一下，自由的射手可能还是居上风的哦!没错，自由是射手的特色，而且也因为这个特色让他们充满了不可预测的魅力，引得狮子心痒痒，真的会有点害怕他们突然之间就飞走了!那才真叫人伤心呢!', '84', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('469', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5L_-ZlSHNGAD3wIB6bUxEyo', '梁英', 'https://wx.qlogo.cn/mmopen/vi_32/Gia2UnCibeVE1icia1Jz77C4xFJUgOuDgBTtqJJSJSs6ibvia7AicNYdfVBLSPWIszMN8AKSy3WucbWKfbBMn9HFXBKwg/132', '摩羯', '../images/staricon/mojie.png', '这是一个非常坚固的组合，不管在什么情况下，这个组合都有很强烈的家庭倾向，因为稳定是他们最大的共同点，所以一旦在一起就会从头稳定到尾，很可能一起步到红毯的另一端! ', '因为两人都以未来为职责，所以就没什么招好过的，只要能够一起经营美好的未来，那些勾心斗角也就不必了，花点心思努力才是重点!这真的不是什么浪漫的组合，当然也有感情不错的一面，但是若说情人节要买花庆祝，对他们而言还不如省下来买房子来得有意义呢! ', '67', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('470', 'oV9VJ5L_-ZlSHNGAD3wIB6bUxEyo', '梁英', 'https://wx.qlogo.cn/mmopen/vi_32/Gia2UnCibeVE1icia1Jz77C4xFJUgOuDgBTtqJJSJSs6ibvia7AicNYdfVBLSPWIszMN8AKSy3WucbWKfbBMn9HFXBKwg/132', '摩羯', '../images/staricon/mojie.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '这是一个势均力敌的组合，几乎可以说是星座中的绝配，因为一旦出现了这种组合，俩人之间绝对没有什么勾心斗角的问题，一定是同心协力地为俩人的未来努力，就算有不一样的意见也没有什么吵架的机会，是一个非常踏实的组合，惟一的缺点可能是有点不够浪漫吧!', '遇到蟹子摩羯可以赶快把心目中未来的远景拿出来实现，让蟹子感到一片美好，充满安全的感觉，那么就再也没什么问题了。如果你在为两个人的未来打拼，那么蟹子一定会全心支持你，让你没有后顾之忧，但是平常的你一定要表现出对他们的重视，让他们安心等你哦!', '67', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('471', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5KE1bsRGpNZEI-aKOp20ZvY', 'Smaly', 'https://wx.qlogo.cn/mmopen/vi_32/j3KAknxqdFM5XqLCvia2etvibuPHbBnHDI4kicCZGnnXhtoTaEzj4koUeJPZABaNSgrjWz33opHKPGZRBFCK8JtyQ/132', '金牛', '../images/staricon/jinniu.png', '这虽然不是大好或大坏的组合，但还算蛮有缘分的，因为这两个星座个性完全不一样，却能互相吸引，不过吸引归吸引，仍然要看两个人互相的配合度才能决定能不能继续下去。', '牛牛是以实际、固执著名的，所以不要和他硬碰硬，那绝对划不来，聪明的羊羊应该赶快去收集数据资料，或想好所谓的“得失利害论”说给牛牛听，相信不用太用力就能说服他了。在爱情上也要表现得条件十足、未来一片光明、充满计划，那牛牛就会觉得安全而放心。', '38', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('472', 'oV9VJ5KE1bsRGpNZEI-aKOp20ZvY', 'Smaly', 'https://wx.qlogo.cn/mmopen/vi_32/j3KAknxqdFM5XqLCvia2etvibuPHbBnHDI4kicCZGnnXhtoTaEzj4koUeJPZABaNSgrjWz33opHKPGZRBFCK8JtyQ/132', '金牛', '../images/staricon/jinniu.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '羊羊和牛牛虽然不算什么大凶组合，但是两个人的个性真是天差地别，相处时不由得让身边的人捏把冷汗!这完全是急惊风遇见慢郎中的配对，怎么样也合不来，但也可以是互补的。', '羊羊抓狂可是很吓人的，不过通常他们都没什么持久性，生完气五分钟后就忘得干干净净；牛牛可以趁这个时候，把刚才没讨论完的事说完，反正这时羊羊也懒得再说什么了，结果一切还是照着牛牛的意愿去完成。这种解决办法既高明又不用伤和气，真是再好也没有了呢! ', '38', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('473', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5Pc7vxokv4XQ_hs7PCWOpXo', '', 'https://wx.qlogo.cn/mmopen/vi_32/lMia17GyQhb6ZraibrNoibqq9ltqHYSpeVKYQwbBfK1vwsUqvgwx2RbayictwB1jqJU0C1zOaazQmgpZqecx3MXqHg/132', '处女', '../images/staricon/chunv.png', '这是个一看就知道差得很多的组合，要不就是冤家路窄，不然就是两个人之间有很强大的吸引力!不过就算他们有着别人所没有的缘分，但是相处上还是多少会有一些冲突发生哦! ', '处女座的“难搞”一直是有目共睹的，不要和他争论，这可是得不偿失的事，因为有时他们只是“觉得”这样做比较好，而羊羊大而化之的性格也要小小地修正一下，稍微细心一点，稍微为处女们做些事就足以收买他们的心了，听起来是有点为难，但是很值得羊羊挑战哦。', '38', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('474', 'oV9VJ5Pc7vxokv4XQ_hs7PCWOpXo', '', 'https://wx.qlogo.cn/mmopen/vi_32/lMia17GyQhb6ZraibrNoibqq9ltqHYSpeVKYQwbBfK1vwsUqvgwx2RbayictwB1jqJU0C1zOaazQmgpZqecx3MXqHg/132', '处女', '../images/staricon/chunv.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '这是一个具有神秘吸引力的组合，尤其是因为这个是完全不一样的星座，所以对彼此产生了完全不同的魅力!不过这并不是一个保证能够长久的配对，能不能有结果就要看缘分了。', '羊羊的天真主动，会让老是定格不动的处女深受吸引，加上羊座是一个非常耀眼的星座，在人群中要不看到他都很难，所以处女会从注意他们到被他们深深地吸引，毕竟和羊羊一样天真又坦率的人真的不多了，会让一直以来不断追求“真、善、美”的处女眼睛一亮。', '38', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('475', 'oV9VJ5HnEZho2CLe3r2BJgcvhW90', '杨阳', 'https://wx.qlogo.cn/mmopen/vi_32/yaA4sCicvRtA9X8vNCvBr7ibA15icUBY4g72pFYxRF2Z03O0XQG3rTkI99GVQwiaHfIrDDZ7oIm4qVFdr9Vbx9VHpQ/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5HCec7oxJZr8NkFFgyBZ-1g', 'lhq', 'https://wx.qlogo.cn/mmopen/vi_32/sNY4r0belHgNYWuBOnPtPe6SBN1gEiaqvXUdOib7yGicEzIffUWdNtyxL63giao6CLXasMtOffG6QC6c9ricUS7xRyg/132', '狮子', '../images/staricon/shizi.png', '这是一个引力超强的组合，会让身旁的人都感受到他们的吸引力，让人很羡慕呢!这个组合是属于互补型的，所以一旦能够速配成功就能长久地经营，不太会因为一点小事就分开。', '狮子和瓶子到底谁强谁弱呢?说真的，不相上下。不过怎么说瓶子也比狮座冷静，所以就算有什么事情狮子也绝对没有瓶子看得清楚。而偏偏瓶子又比狮座来得冷血许多，如果真的出了什么事，那么跳脚的百分之八十是狮座，瓶子只会像没事一样站在那边等结果。', '52', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('476', 'oV9VJ5HCec7oxJZr8NkFFgyBZ-1g', 'lhq', 'https://wx.qlogo.cn/mmopen/vi_32/sNY4r0belHgNYWuBOnPtPe6SBN1gEiaqvXUdOib7yGicEzIffUWdNtyxL63giao6CLXasMtOffG6QC6c9ricUS7xRyg/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5HnEZho2CLe3r2BJgcvhW90', '杨阳', 'https://wx.qlogo.cn/mmopen/vi_32/yaA4sCicvRtA9X8vNCvBr7ibA15icUBY4g72pFYxRF2Z03O0XQG3rTkI99GVQwiaHfIrDDZ7oIm4qVFdr9Vbx9VHpQ/132', '水瓶', '../images/staricon/shuiping.png', '这是一个能互相吸引的星座，尤其是双方不但有完全一样的地方足以互补，也有非常相像的地方可以合作，如果能够在一起，就是让人羡慕的组合哦!这也是一个常见的配对。', '对于瓶子这种我行我素的星座，要引起他们的注意其实不难，只要和别人不一样就行了，不过要他们专情于你是不太容易的，因为他们太过好奇，所以你要永远保持新鲜感，让他们对你看不腻，那就会比较顺利了。不过最重要的是，千万不要限制瓶瓶视若生命的自由哦!', '52', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('477', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5K_I3vA9CaOj1ECFiyUYMN0', '张会计', 'https://wx.qlogo.cn/mmopen/vi_32/KxafqL5JxKTr6D093G9dqEicw4SC895HRLTN8cQfQibGxSicvlIBG2zDVwfEjPypBaLf58SEESNsInyiaiahIiaptK8g/132', '狮子', '../images/staricon/shizi.png', '这个配对是很适合组合家庭的，因为这两个星座都非常传统，也都很重视家庭，所以只要两个人能在一起就会一起规划日后家庭的蓝图，是非常稳固的配对，适合一起步入婚姻。', '蟹子如果想摆平狮子真是易如反掌!道理很简单，因为蟹子很尊重狮子的决定，就算不同意也不会让狮子下不了台，给狮子留足了面子，不但保障了两个人的感情，也赢得了狮座对蟹子的喜爱与信任，真是何乐而不为呢?说真的，这才是最聪明的办法，蟹子一定要记住哦!', '73', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('478', 'oV9VJ5DAKqP-sNzbYx6Ib7_BwgTg', '肉丸', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKE75UplojzkicUv9TWNFZibM1PjkUC2kaKNaOCRcZ6Ku2qSHNDBvdy1VItgib2pI4nC8ZvDkvkFxXmQ/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5Pu8rKTBMQynvsICN_8YP78', 'AMPULLA', 'https://wx.qlogo.cn/mmopen/vi_32/BVVpsAPhrLjYgB2oePP5VsgjqN6WBYMbz4kEFj7MtwfxBWxQia28EL7CzMg2wuast43BF9pvDSJPMfYicu57WZGQ/132', '狮子', '../images/staricon/shizi.png', '这是一个让人非常头痛的组合，简直就验证了\"一山不容二虎\"这句话，凭着他们两个都具有天生的王者气息，就足以构成一场很不好打的仗，不成为死对头就该偷笑了! ', '要把狮子解决掉实在是很简单的事，蝎子不是不会说好听的话，而是不屑说，可是一旦蝎子要耍阴谋，那什么样的事他们都做得出来。对蝎子而言要安抚狮子实在是一件很容易的事，只是看蝎子要不要和狮子搅和而已，蝎子就会以使使小计谋来吃定狮子。', '61', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('479', 'oV9VJ5Pu8rKTBMQynvsICN_8YP78', 'AMPULLA', 'https://wx.qlogo.cn/mmopen/vi_32/BVVpsAPhrLjYgB2oePP5VsgjqN6WBYMbz4kEFj7MtwfxBWxQia28EL7CzMg2wuast43BF9pvDSJPMfYicu57WZGQ/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5DAKqP-sNzbYx6Ib7_BwgTg', '肉丸', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKE75UplojzkicUv9TWNFZibM1PjkUC2kaKNaOCRcZ6Ku2qSHNDBvdy1VItgib2pI4nC8ZvDkvkFxXmQ/132', '天蝎', '../images/staricon/tianxie.png', '这也是一个战况颇激烈的组合，因为双方都有当\"王者\"的特质，不争得你死我活实在是太对不起自己了!这种配对的两人风格不但完全不同，就连对事情的看法也很不一样哦! ', '虽然两人可以说有些不相上下，不过若真的要论输赢的话，狮子可能略逊天蝎一筹哦!为什么呢?因为如果狮子算是来明的，那么蝎子绝对是选择耍阴的!没错，天蝎就是黑暗的帝王，不是说\"暗箭难防\"吗?所以狮子座遇到这么阴狠的天蝎座，可能就英雄无用武之地了!', '61', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('480', 'oV9VJ5K_I3vA9CaOj1ECFiyUYMN0', '张会计', 'https://wx.qlogo.cn/mmopen/vi_32/KxafqL5JxKTr6D093G9dqEicw4SC895HRLTN8cQfQibGxSicvlIBG2zDVwfEjPypBaLf58SEESNsInyiaiahIiaptK8g/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '这是一个既稳定又能长久的组合，因为两人对于感情的看法是一样的，加上两个人都拥有希望稳定下来的个性，通常只要能速配成功就很难拆散他们了，尤其当狮子座是男方的时候。', '巨蟹座什么都不缺，就是缺乏安全感!这一点狮子座一定要明白，而且要拿来当护身符!只要狮子座能让蟹子感到安全无虑，就已经及格一半了，另一半还得加上幸福的保障!所以在巨蟹座面前狮子一定要专情，而且要一副很了解自己要做什么的模样，才能让多疑的蟹子解除心防。', '73', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('481', 'oV9VJ5DAKqP-sNzbYx6Ib7_BwgTg', '肉丸', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKE75UplojzkicUv9TWNFZibM1PjkUC2kaKNaOCRcZ6Ku2qSHNDBvdy1VItgib2pI4nC8ZvDkvkFxXmQ/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5DBFH3jByP9e_VncPKRmn5o', 'JaXn', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK1Vs58mvd92VQxOFAd4VdILhXj6mdpTsaTsqjH4epgN5x7PFZWe9ZSEUlzfHy3yDJX2qCvGq905A/132', '双子', '../images/staricon/shuangzi.png', '这是一个很容易产生火花的组合，尤其是两人都觉得征服对方是个伟大的挑战，所以因想要征服对方而在一起的可能性很大。这两个星座在一起会有神秘的吸引力，能否长久则很难说。', '双子其实比天蝎来得善良多了，因为他们真的只是玩玩而已，从来没想过要达到什么效果之类的，但是天蝎可就没这么简单了，什么事都有目的。所以蝎子不用紧张，只要稳稳抓住双子的好奇心，再好好吊吊胃口，双子就上钩了，只不过可以持续多久就要看蝎子的功力了。', '29', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('482', 'oV9VJ5DBFH3jByP9e_VncPKRmn5o', 'JaXn', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK1Vs58mvd92VQxOFAd4VdILhXj6mdpTsaTsqjH4epgN5x7PFZWe9ZSEUlzfHy3yDJX2qCvGq905A/132', '双子', '../images/staricon/shuangzi.png', 'oV9VJ5DAKqP-sNzbYx6Ib7_BwgTg', '肉丸', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKE75UplojzkicUv9TWNFZibM1PjkUC2kaKNaOCRcZ6Ku2qSHNDBvdy1VItgib2pI4nC8ZvDkvkFxXmQ/132', '天蝎', '../images/staricon/tianxie.png', '这是一个充满吸引力的组合，两个星座都充满挑战性，在一起不但会引起挑战欲与好胜心，更能激发出热情的火花，虽然外人看起来格格不入，但却是很有缘分和特殊魅力的组合哦!', '蝎子既神秘又难被吸引，对双子来说是少有的难题，其实双子只要展现自己善变的魅力加上无拘无束的气质，保证蝎子一定会注意到你，等到蝎座开始对你注目，双子再玩玩欲擒故纵的游戏，让蝎子主动接近，你们的距离自然就会缩短了，蝎座也会展现他们不同的一面。', '29', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('483', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5Oufak1hban6eteOEub_F5c', '蔡钰', 'https://wx.qlogo.cn/mmopen/vi_32/qQWCrnjSfeacOzygUXM8F8vP7VTDDe8VkB50NyzaZPEyjQypicTM9yhc0ic0D76eb4NB5o9ktdS0jbAlX7LQAwDQ/132', '天秤', '../images/staricon/tianping.png', '这是个很不一样但又能互相协调的配对，两个人有着密不可分的关系，但最重要的是必须彼此尊重，感情才能长久维持下去。通常这种组合的缘分都很深厚，值得好好花心力经营。', '羊羊的自我和秤子的随和正好相反，不过羊座要注意秤子是个重视公平的星座，如果你希望秤子为你做什么，那么你也要让他们觉得你为他做了什么，不要一味地任性，久了秤子是不吃你这一套的!没事多听听秤子的意见，就算他不说也要问，你才能知道如何跟他相处。', '64', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('484', 'oV9VJ5Oufak1hban6eteOEub_F5c', '蔡钰', 'https://wx.qlogo.cn/mmopen/vi_32/qQWCrnjSfeacOzygUXM8F8vP7VTDDe8VkB50NyzaZPEyjQypicTM9yhc0ic0D76eb4NB5o9ktdS0jbAlX7LQAwDQ/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '这是一个会互相吸引的配对，别人看起来两个人虽然天差地别，不过事实上他们重视的事情还是有很多是相同的。所以只要没有恶劣到如敌人般仇视，成为亲密战友的日子则指日可待!', '秤子要对付白羊其实不会很难的，因为羊羊不是个很\"不讲理\"的星座，该讲道理的时候他们也听得懂，只是不太能理解就是了。那秤子就做给他们看，这样羊羊就会理解，只是时间要久一点。总之，秤子只要气定神闲就好了，不管羊羊到底抓狂到什么地步，安心等他气消回来吧!', '64', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('485', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5NLpi-9Ngq_ebrDPvfLBCMo', '樱木', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKGtLdyAMWX5pYKEGQAwcWhEibib5AWl7hcexS7JOkERPb2cFiaEuhQLPx81RRCOY7mFGS0INHVFmlbg/132', '摩羯', '../images/staricon/mojie.png', '这是一个非常坚固的组合，不管在什么情况下，这个组合都有很强烈的家庭倾向，因为稳定是他们最大的共同点，所以一旦在一起就会从头稳定到尾，很可能一起步到红毯的另一端! ', '因为两人都以未来为职责，所以就没什么招好过的，只要能够一起经营美好的未来，那些勾心斗角也就不必了，花点心思努力才是重点!这真的不是什么浪漫的组合，当然也有感情不错的一面，但是若说情人节要买花庆祝，对他们而言还不如省下来买房子来得有意义呢! ', '66', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('486', 'oV9VJ5NLpi-9Ngq_ebrDPvfLBCMo', '樱木', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKGtLdyAMWX5pYKEGQAwcWhEibib5AWl7hcexS7JOkERPb2cFiaEuhQLPx81RRCOY7mFGS0INHVFmlbg/132', '摩羯', '../images/staricon/mojie.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '这是一个势均力敌的组合，几乎可以说是星座中的绝配，因为一旦出现了这种组合，俩人之间绝对没有什么勾心斗角的问题，一定是同心协力地为俩人的未来努力，就算有不一样的意见也没有什么吵架的机会，是一个非常踏实的组合，惟一的缺点可能是有点不够浪漫吧!', '遇到蟹子摩羯可以赶快把心目中未来的远景拿出来实现，让蟹子感到一片美好，充满安全的感觉，那么就再也没什么问题了。如果你在为两个人的未来打拼，那么蟹子一定会全心支持你，让你没有后顾之忧，但是平常的你一定要表现出对他们的重视，让他们安心等你哦!', '66', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('487', 'oV9VJ5HnEZho2CLe3r2BJgcvhW90', '杨阳', 'https://wx.qlogo.cn/mmopen/vi_32/yaA4sCicvRtA9X8vNCvBr7ibA15icUBY4g72pFYxRF2Z03O0XQG3rTkI99GVQwiaHfIrDDZ7oIm4qVFdr9Vbx9VHpQ/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5G9gx5b-59uyhmR5pQvCCMA', '燕子', 'https://wx.qlogo.cn/mmopen/vi_32/ysYKKdicaqYjUib1C252IF5Il9e0Bu9ibTQiabHxTuUNvK6piaUI6LnQCV0aOTToI7Ls7HTgR9UWFRbJgf611fSWCvg/132', '水瓶', '../images/staricon/shuiping.png', '瓶子对上瓶子算是完美的朋友组合，但是如果变成情人那就不太一定了，毕竟对两个爱好自由的人来说，当朋友才是最好的距离，不然在一起老是聚少离多，就没有在一起的必要了。', '瓶子对瓶子来说真是没什么好说的，反正都一样嘛，还有什么强弱之分呢?最重要的应该是两个人有没有地方可以一拍即合，再不满意也挑不出毛病来；一旦哪天不合适了，两人也没有什么死缠烂打的可能，反而是分手后还是朋友的代表。', '62', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('488', 'oV9VJ5G9gx5b-59uyhmR5pQvCCMA', '燕子', 'https://wx.qlogo.cn/mmopen/vi_32/ysYKKdicaqYjUib1C252IF5Il9e0Bu9ibTQiabHxTuUNvK6piaUI6LnQCV0aOTToI7Ls7HTgR9UWFRbJgf611fSWCvg/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5HnEZho2CLe3r2BJgcvhW90', '杨阳', 'https://wx.qlogo.cn/mmopen/vi_32/yaA4sCicvRtA9X8vNCvBr7ibA15icUBY4g72pFYxRF2Z03O0XQG3rTkI99GVQwiaHfIrDDZ7oIm4qVFdr9Vbx9VHpQ/132', '水瓶', '../images/staricon/shuiping.png', '瓶子对上瓶子算是完美的朋友组合，但是如果变成情人那就不太一定了，毕竟对两个爱好自由的人来说，当朋友才是最好的距离，不然在一起老是聚少离多，就没有在一起的必要了。', '瓶子对瓶子来说真是没什么好说的，反正都一样嘛，还有什么强弱之分呢?最重要的应该是两个人有没有地方可以一拍即合，再不满意也挑不出毛病来；一旦哪天不合适了，两人也没有什么死缠烂打的可能，反而是分手后还是朋友的代表。', '62', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('489', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5Ood3q1M1STJhV1yU0tXkZU', '小巩', 'https://wx.qlogo.cn/mmopen/vi_32/2WiaxYZbNdT6ZswbuBD2MPkwwwmBQTZd0CSibhOTVEu6STRkNg0iaL9VH2Ht9TtKAXPfZDsMwLWE6toHurszmnN0g/132', '处女', '../images/staricon/chunv.png', '这是个一看就知道差得很多的组合，要不就是冤家路窄，不然就是两个人之间有很强大的吸引力!不过就算他们有着别人所没有的缘分，但是相处上还是多少会有一些冲突发生哦! ', '处女座的“难搞”一直是有目共睹的，不要和他争论，这可是得不偿失的事，因为有时他们只是“觉得”这样做比较好，而羊羊大而化之的性格也要小小地修正一下，稍微细心一点，稍微为处女们做些事就足以收买他们的心了，听起来是有点为难，但是很值得羊羊挑战哦。', '21', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('490', 'oV9VJ5Ood3q1M1STJhV1yU0tXkZU', '小巩', 'https://wx.qlogo.cn/mmopen/vi_32/2WiaxYZbNdT6ZswbuBD2MPkwwwmBQTZd0CSibhOTVEu6STRkNg0iaL9VH2Ht9TtKAXPfZDsMwLWE6toHurszmnN0g/132', '处女', '../images/staricon/chunv.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '这是一个具有神秘吸引力的组合，尤其是因为这个是完全不一样的星座，所以对彼此产生了完全不同的魅力!不过这并不是一个保证能够长久的配对，能不能有结果就要看缘分了。', '羊羊的天真主动，会让老是定格不动的处女深受吸引，加上羊座是一个非常耀眼的星座，在人群中要不看到他都很难，所以处女会从注意他们到被他们深深地吸引，毕竟和羊羊一样天真又坦率的人真的不多了，会让一直以来不断追求“真、善、美”的处女眼睛一亮。', '21', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('491', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5Ec3k8E1bHzmZSW1JAtne8o', '左云', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJDZJCdn1xIDoLNpLMiaVwm7UD8WWYDBT0JbSwe7HYDnn9wD0fEZclsbT3wjl8FSGaVboZ7ibgXqf5A/132', '射手', '../images/staricon/sheshou.png', '这是一个非常好的组合，个性都同样的热情大胆，发展出来的恋情也是既热情又浪漫!感情顺利之外，两人还有类似的想法和个性，所以相处起来非常自在，是个很圆满的配对哦! ', '射手就是喜欢白羊天真直率的样子，所以羊羊不必想太多，保持自然本性就可以了，至于射手座喜爱自由的个性，羊羊也不必太介意，不然跟着他到处跑也行，反正给他足够的自由，和他在一起的时候让他觉得轻松自在，让他什么话都可以跟你聊，那就成功了一半啦!', '81', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('492', 'oV9VJ5Ec3k8E1bHzmZSW1JAtne8o', '左云', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJDZJCdn1xIDoLNpLMiaVwm7UD8WWYDBT0JbSwe7HYDnn9wD0fEZclsbT3wjl8FSGaVboZ7ibgXqf5A/132', '射手', '../images/staricon/sheshou.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '这是一个活力四射的组合，不仅一拍即合，而且进展得很快，因为两个人都是单纯、没有心机的星座，所以相处起来非常的直接，总是火辣辣地让每个人羡慕不已，是一个超级般配组合。', '射手从来没见过像羊座这么坦白的人呢!让射手们和羊座在一起的时候觉得全身舒畅，加上羊羊与生俱来的性感魅力，叫射手们不着迷都难呢!也就是因为羊羊具有这么跌破射手们眼镜的单纯直接，总是可以在一群人当中立刻引起射手的注意，让射手们兴起征服的欲望。', '81', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('493', 'oV9VJ5Oufak1hban6eteOEub_F5c', '蔡钰', 'https://wx.qlogo.cn/mmopen/vi_32/qQWCrnjSfeacOzygUXM8F8vP7VTDDe8VkB50NyzaZPEyjQypicTM9yhc0ic0D76eb4NB5o9ktdS0jbAlX7LQAwDQ/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5EeVXvZyvsdwWLD-Ao2zPLI', 'Aries', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTIwXolQw3pVOus20Rpu8VyHLAOvb1RF5Ox6QhOAnQvpDAcdyW5psqgmGkdCGfWc5e3HD2aPLITsWQ/132', '白羊', '../images/staricon/baiyang.png', '这是一个会互相吸引的配对，别人看起来两个人虽然天差地别，不过事实上他们重视的事情还是有很多是相同的。所以只要没有恶劣到如敌人般仇视，成为亲密战友的日子则指日可待!', '秤子要对付白羊其实不会很难的，因为羊羊不是个很\"不讲理\"的星座，该讲道理的时候他们也听得懂，只是不太能理解就是了。那秤子就做给他们看，这样羊羊就会理解，只是时间要久一点。总之，秤子只要气定神闲就好了，不管羊羊到底抓狂到什么地步，安心等他气消回来吧!', '75', '1', '0');
