INSERT INTO `mn_xz_recard` VALUES ('689', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5HRICKKoVlc7RtmcvwFJ7B4', '竹林笑傲', 'https://wx.qlogo.cn/mmopen/vi_32/ia3mKkHTKhZaibNGBLmKVzjAEvG0fLHnhZNbe4sPvghOZlPTIBUkkSsvrBNBMiau9lCZYE91mrTJsVYsBe6xtW47g/132', '双鱼', '../images/staricon/shuangyu.png', '这可是一个超级浪漫的组合，他们不切实际的程度足以媲美\"灰姑娘\"的际遇，这个配对真的很容易变得不切实际，有可能有了爱情就不要面包，不过却是非常非常的惟美浪漫哦! ', '蟹子的全心付出会让鱼座非常感动，觉得无以为报，所以蟹子要做的事就是不断付出自己丰沛的爱心，让鱼座溺死在里面，连翻身的机会都没有，加上鱼座对巨蟹日渐变得依赖，这样一来鱼鱼就再也跑不掉了，而这个美妙的爱情配对，自然就会有幸福美满的结局出现了! ', '93', '0', '1');
INSERT INTO `mn_xz_recard` VALUES ('690', 'oV9VJ5HRICKKoVlc7RtmcvwFJ7B4', '竹林笑傲', 'https://wx.qlogo.cn/mmopen/vi_32/ia3mKkHTKhZaibNGBLmKVzjAEvG0fLHnhZNbe4sPvghOZlPTIBUkkSsvrBNBMiau9lCZYE91mrTJsVYsBe6xtW47g/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '遇到蟹子，鱼座惟一要做的就是把蟹子心底想的摸个透彻，因为蟹子很需要别人了解和谅解，而鱼座正好具有这种同情心旺盛的特质，加上似水的柔情，不用一时三刻，蟹子不上钩都难。不过也是因为这两个星座天生有互相吸引的特质，所以实在是不用动太多的脑筋。', '一个这么深情的组合还有什么好比的?不过若是在爱情的依赖度上，鱼鱼对巨蟹座有着莫名的依赖感，可能是因为巨蟹具有母亲本能吧!这种包容和体贴，是鱼座软弱时最好的避风港，鱼鱼其实是很脆弱的，受不得大风大浪，蟹子的两个大螯正好提供了他们最好的保证。', '93', '0', '0');
INSERT INTO `mn_xz_recard` VALUES ('691', 'oV9VJ5JNp0vLf6riCbZ_V8xF2-60', '李正烨', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTI7YXeW9y73IibEEZBsibfJLaZgcxoiachXHzGics0NBwWG6qZkKmyJJ0Y87ibuwkjguiaImD5ZjrXic3hjg/132', '处女', '../images/staricon/chunv.png', 'oV9VJ5Jsw_tvNT6w6386NKkfBLyE', '守候。', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTIBlIbh22D3GB6RJRL93Gf5ERDETRKiboELkKYbQV2ZTQOcAweuLdw2HNrOpwajk8u98ttpqUicpicyw/132', '白羊', '../images/staricon/baiyang.png', '这是一个具有神秘吸引力的组合，尤其是因为这个是完全不一样的星座，所以对彼此产生了完全不同的魅力!不过这并不是一个保证能够长久的配对，能不能有结果就要看缘分了。', '羊羊的天真主动，会让老是定格不动的处女深受吸引，加上羊座是一个非常耀眼的星座，在人群中要不看到他都很难，所以处女会从注意他们到被他们深深地吸引，毕竟和羊羊一样天真又坦率的人真的不多了，会让一直以来不断追求“真、善、美”的处女眼睛一亮。', '20', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('692', 'oV9VJ5Jsw_tvNT6w6386NKkfBLyE', '守候。', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTIBlIbh22D3GB6RJRL93Gf5ERDETRKiboELkKYbQV2ZTQOcAweuLdw2HNrOpwajk8u98ttpqUicpicyw/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5JNp0vLf6riCbZ_V8xF2-60', '李正烨', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTI7YXeW9y73IibEEZBsibfJLaZgcxoiachXHzGics0NBwWG6qZkKmyJJ0Y87ibuwkjguiaImD5ZjrXic3hjg/132', '处女', '../images/staricon/chunv.png', '这是个一看就知道差得很多的组合，要不就是冤家路窄，不然就是两个人之间有很强大的吸引力!不过就算他们有着别人所没有的缘分，但是相处上还是多少会有一些冲突发生哦! ', '处女座的“难搞”一直是有目共睹的，不要和他争论，这可是得不偿失的事，因为有时他们只是“觉得”这样做比较好，而羊羊大而化之的性格也要小小地修正一下，稍微细心一点，稍微为处女们做些事就足以收买他们的心了，听起来是有点为难，但是很值得羊羊挑战哦。', '20', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('693', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5Md7awoALAPX5XP-l09g0cU', 'Selina', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eribZbBKs7E0x9f6GkmgGDbesWicCXlNZjRbxuSqI3khoMicLVAZ6kpkUPpLQIZIXp5B253zFr2rLpjA/132', '水瓶', '../images/staricon/shuiping.png', '这是个非常稳定的组合哦!主要是因为彼此都有坚固的友谊作为感情的基础，就算有什么摩擦也能很快地消除。互相信任是让感情长久最重要的一环，也是这个配对最重要的特质。', '羊羊对朋友的绝对信任会让瓶子觉得找到了知己，虽然羊羊并不一定了解瓶子，但是却能给瓶子很大的安全感。也许瓶子是一个很容易让人误会的角色，不喜欢解释的他们，遇到能够无条件支持自己的对象，会觉得很感动，而且要记住，对瓶子来说，是朋友才能当情人。', '79', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('694', 'oV9VJ5Md7awoALAPX5XP-l09g0cU', 'Selina', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83eribZbBKs7E0x9f6GkmgGDbesWicCXlNZjRbxuSqI3khoMicLVAZ6kpkUPpLQIZIXp5B253zFr2rLpjA/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5GNhaCnjV1n4x5HrxBCprKA', '毛毛', 'https://wx.qlogo.cn/mmopen/vi_32/42bHniblKXyjicCjjro9KR5JNAiaqUj77NyPekvFlpYMWBheel0yM4tuNdowibH7NxOZSwIJuaicOB3bj543dkBrl1Q/132', '白羊', '../images/staricon/baiyang.png', '这是一个坚固的友情组合，如果能够升华成爱情绝对和两个人紧密的友情有关。互相信任是这个配对最大的特色，是个相当和谐的速配组合。', '遇到羊羊，瓶瓶只要按兵不动就好了，羊羊的速度可是谁也追不上的，瓶子动也不动的呆样，足以让羊羊关心地围绕在你身边，面对羊羊，瓶瓶只要多多发挥自己动人的智慧和口才就足以把羊羊骗到手了。', '79', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('695', 'oV9VJ5FF3EjWFyHPzXMHJ9IuNh-U', '��', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLUswfXViaNb3k2vQu8tm2DwmPn0ereCxPNwkOVM4fUpQyGdSUnP8HO3NOzGqGhiaPjyxzjXhXmuuwg/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5KxOGUMvf6jrcAb9_RMD9Lw', '酱酱酱酱', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJ2kJrcnEE3A6orYnoZeAicRkylv7MK6icgffgEbrWibBGC6zo9iaicsIpGSfMDQmdqXsAazLU7q5WiafCg/132', '水瓶', '../images/staricon/shuiping.png', '瓶子对上瓶子算是完美的朋友组合，但是如果变成情人那就不太一定了，毕竟对两个爱好自由的人来说，当朋友才是最好的距离，不然在一起老是聚少离多，就没有在一起的必要了。', '瓶子对瓶子来说真是没什么好说的，反正都一样嘛，还有什么强弱之分呢?最重要的应该是两个人有没有地方可以一拍即合，再不满意也挑不出毛病来；一旦哪天不合适了，两人也没有什么死缠烂打的可能，反而是分手后还是朋友的代表。', '76', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('696', 'oV9VJ5KxOGUMvf6jrcAb9_RMD9Lw', '酱酱酱酱', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJ2kJrcnEE3A6orYnoZeAicRkylv7MK6icgffgEbrWibBGC6zo9iaicsIpGSfMDQmdqXsAazLU7q5WiafCg/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5FF3EjWFyHPzXMHJ9IuNh-U', '��', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLUswfXViaNb3k2vQu8tm2DwmPn0ereCxPNwkOVM4fUpQyGdSUnP8HO3NOzGqGhiaPjyxzjXhXmuuwg/132', '水瓶', '../images/staricon/shuiping.png', '瓶子对上瓶子算是完美的朋友组合，但是如果变成情人那就不太一定了，毕竟对两个爱好自由的人来说，当朋友才是最好的距离，不然在一起老是聚少离多，就没有在一起的必要了。', '瓶子对瓶子来说真是没什么好说的，反正都一样嘛，还有什么强弱之分呢?最重要的应该是两个人有没有地方可以一拍即合，再不满意也挑不出毛病来；一旦哪天不合适了，两人也没有什么死缠烂打的可能，反而是分手后还是朋友的代表。', '76', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('697', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5PrrS3L7TFM62nHVYRtgCg8', '不会月半', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKFAhFZVic4WS6Pkok89GrEA3yMrOjYMCxtY9Watj3cjwKfv4mwEOfTozDjR9jzUXLAo0TBnJw46qg/132', '天秤', '../images/staricon/tianping.png', '这是一个很能互相了解的组合，两个星座都很执著于完美，所以很了解对方内心的矛盾。这个组合适合从朋友开始发展，虽然没什么热烈的感觉，但是却很温暖，充满相知相惜的感觉。', '这个配对中蝎子很明显地比秤子强很多，因为天蝎是不太管别人死活的，但是秤子很在乎别人的感受，这样下来，当然是秤子来迁就蝎子了!而且蝎子神秘的特质正好是秤子最喜欢的，天蝎不断地引起秤子强烈的好奇心，让两人的关系老是一面倒，而且是由蝎子主控着。', '42', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('698', 'oV9VJ5PrrS3L7TFM62nHVYRtgCg8', '不会月半', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKFAhFZVic4WS6Pkok89GrEA3yMrOjYMCxtY9Watj3cjwKfv4mwEOfTozDjR9jzUXLAo0TBnJw46qg/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', '这个星座组合其实还蛮合适的，两者虽然完全不同，但是看起来又很相配，都是很有魅力的星座，而且这两个星座的个性正好互补得恰到好处，如果有缘分就会是速配成功的组合!', '如果想吃定蝎座，那秤子可就不能懒散地过日子了，当然，你可不能被他看出你在备战。蝎子看起来冷静，其实是座火山，如果你气氛拿捏适当，那么成功一定是指日可待的。不要主动质问蝎座，自己小心地观察，你就会看得出来他们到底有没有上钩。', '42', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('699', 'oV9VJ5PrrS3L7TFM62nHVYRtgCg8', '不会月半', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKFAhFZVic4WS6Pkok89GrEA3yMrOjYMCxtY9Watj3cjwKfv4mwEOfTozDjR9jzUXLAo0TBnJw46qg/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5LLVHsuHhbuR22u6ejy2hLk', '', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELxantDurHEUm6UVNe1PUvf0vc5lakEcfT9bpB5wvLicXYmFQdANiahoPPlkFJuEicYb3dGNWp32GR4A/132', '天秤', '../images/staricon/tianping.png', '这个组合并不是最好的，但因为星座相同，所以也可能有很合的地方，不过因为他们的决策力都不好，所以一旦遇到需要决定的事情那就很麻烦了。这个配对成功的可能性并不高。', '双方都是同样的星座对你们来说其实有点困扰，因为秤子是天生喜欢配合别人的星座，如果两个人都打算配合对方的话，那到底要配合谁呢?真是让人颇为伤脑筋呢!而且秤子天生就懒懒散散的，两个人如果一起懒，那大概是连家门都走不出去了吧，生活上面临的问题可真不少呢! ', '76', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('700', 'oV9VJ5LLVHsuHhbuR22u6ejy2hLk', '', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELxantDurHEUm6UVNe1PUvf0vc5lakEcfT9bpB5wvLicXYmFQdANiahoPPlkFJuEicYb3dGNWp32GR4A/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5PrrS3L7TFM62nHVYRtgCg8', '不会月半', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKFAhFZVic4WS6Pkok89GrEA3yMrOjYMCxtY9Watj3cjwKfv4mwEOfTozDjR9jzUXLAo0TBnJw46qg/132', '天秤', '../images/staricon/tianping.png', '这个组合并不是最好的，但因为星座相同，所以也可能有很合的地方，不过因为他们的决策力都不好，所以一旦遇到需要决定的事情那就很麻烦了。这个配对成功的可能性并不高。', '双方都是同样的星座对你们来说其实有点困扰，因为秤子是天生喜欢配合别人的星座，如果两个人都打算配合对方的话，那到底要配合谁呢?真是让人颇为伤脑筋呢!而且秤子天生就懒懒散散的，两个人如果一起懒，那大概是连家门都走不出去了吧，生活上面临的问题可真不少呢! ', '76', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('701', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5EMKOWwlQehOagxAy-AQGXw', 'YU青格', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJV07WOuzNCBgHG4HRFrDCIibsteWLhOF8WFVswyQOuCgM8t5ZfHPQoib3eqRdfBiaWoz49ibFAJ9QIBg/132', '金牛', '../images/staricon/jinniu.png', '这是一个结合得很紧密的配对。尤其是当这个配对成功了，那要拆散他们可能比登天还难。这两个都是意志力非常坚强的星座，一旦达成共识，就会是无可动摇的，是长久的组合。', '这个配对中，令人意外的是牛牛竟然占了上风，因为牛座是不会因为蝎座的勾引而心痒难耐的，蝎子要多走几步，牛座才会迈着牛步慢慢跟上，这种状况简直跌破蝎子的眼镜，不得不开始摆明着来，老神经兮兮的牛座才开始考虑打算，真把蝎座急死了，完全失去了冷静的特色。', '52', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('702', 'oV9VJ5EMKOWwlQehOagxAy-AQGXw', 'YU青格', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJV07WOuzNCBgHG4HRFrDCIibsteWLhOF8WFVswyQOuCgM8t5ZfHPQoib3eqRdfBiaWoz49ibFAJ9QIBg/132', '金牛', '../images/staricon/jinniu.png', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', '这也是一个很特别的组合，虽然充满了别人不了解的吸引力，但是在一起的结果却很难定论好坏，因为两人的个性差异很大，这个特征不但是互相吸引的原因，也是造成不合的因素。', '其实两个人的强弱没有差很多，不过一般而言，牛座的稳定气质让蝎座非常喜欢，加上牛牛对自己充满着自主，蝎座很容易就会注意到牛座。这个组合的两个星座都很具有个人魅力，牛座的特色对蝎座来说充满了吸引他接近的诱惑力，而牛牛反而比较常是被动的一方。', '52', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('703', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5KQFzci94laVhGuflNjXFEg', '☆疯子☆', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJU3y8AKFBmn2YXy87syIup2r6dQ7JmVP4uNSSby7jNt5sH1pUgH2MiclfsjluolerjtQMkjpyGa0A/132', '双子', '../images/staricon/shuangzi.png', '这是一个很容易产生火花的组合，尤其是两人都觉得征服对方是个伟大的挑战，所以因想要征服对方而在一起的可能性很大。这两个星座在一起会有神秘的吸引力，能否长久则很难说。', '双子其实比天蝎来得善良多了，因为他们真的只是玩玩而已，从来没想过要达到什么效果之类的，但是天蝎可就没这么简单了，什么事都有目的。所以蝎子不用紧张，只要稳稳抓住双子的好奇心，再好好吊吊胃口，双子就上钩了，只不过可以持续多久就要看蝎子的功力了。', '22', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('704', 'oV9VJ5KQFzci94laVhGuflNjXFEg', '☆疯子☆', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTJU3y8AKFBmn2YXy87syIup2r6dQ7JmVP4uNSSby7jNt5sH1pUgH2MiclfsjluolerjtQMkjpyGa0A/132', '双子', '../images/staricon/shuangzi.png', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', '这是一个充满吸引力的组合，两个星座都充满挑战性，在一起不但会引起挑战欲与好胜心，更能激发出热情的火花，虽然外人看起来格格不入，但却是很有缘分和特殊魅力的组合哦!', '蝎子既神秘又难被吸引，对双子来说是少有的难题，其实双子只要展现自己善变的魅力加上无拘无束的气质，保证蝎子一定会注意到你，等到蝎座开始对你注目，双子再玩玩欲擒故纵的游戏，让蝎子主动接近，你们的距离自然就会缩短了，蝎座也会展现他们不同的一面。', '22', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('705', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5HL0Gas999elrn3ztPnFftA', '洛田', 'https://wx.qlogo.cn/mmopen/vi_32/Ze4sKMevwunY7amY9YxGvwY450OOcMlBuib2FqVXhjqcQ4R1erBNXnTfXoxs7iboW584W4C9P1DxticpNHnkzyia5g/132', '双鱼', '../images/staricon/shuangyu.png', '同星座嘛没什么可以比较的，不过鱼鱼分两种，一种是现实派，但心里有着对于理想的浪漫想法；另一种是梦幻派，天天做梦不切实际，通常会和现实社会脱节!没办法，鱼鱼是天生的极端，无法把两样事调配好，但是一遇到爱情，所有的鱼都一样有着无可救药的浪漫情怀。', '当鱼遇到鱼的时候最重要的事是了解自己在想什么，了解了自己就是了解对方啦!不过你们的问题是一样的，虽然无法替对方解答倒可以同病相怜一番，也可能因为这种情怀而让鱼鱼觉得爱上了对方，进而牺牲奉献地想尽一己之力，但过多的同情对鱼来说只会更沉沦罢了。', '86', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('706', 'oV9VJ5HL0Gas999elrn3ztPnFftA', '洛田', 'https://wx.qlogo.cn/mmopen/vi_32/Ze4sKMevwunY7amY9YxGvwY450OOcMlBuib2FqVXhjqcQ4R1erBNXnTfXoxs7iboW584W4C9P1DxticpNHnkzyia5g/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', '同星座嘛没什么可以比较的，不过鱼鱼分两种，一种是现实派，但心里有着对于理想的浪漫想法；另一种是梦幻派，天天做梦不切实际，通常会和现实社会脱节!没办法，鱼鱼是天生的极端，无法把两样事调配好，但是一遇到爱情，所有的鱼都一样有着无可救药的浪漫情怀。', '当鱼遇到鱼的时候最重要的事是了解自己在想什么，了解了自己就是了解对方啦!不过你们的问题是一样的，虽然无法替对方解答倒可以同病相怜一番，也可能因为这种情怀而让鱼鱼觉得爱上了对方，进而牺牲奉献地想尽一己之力，但过多的同情对鱼来说只会更沉沦罢了。', '86', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('707', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5GxZBX1kqdPviUFNjAv-ZMA', 'Lim', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83epsDm8xWLeqSYv9x5TfsSSIWicAu3Z6b9q1bddSL3MNxW7R3otMAbxSNGzCpBhOQCjQyvgFhpb2Q2Q/132', '天蝎', '../images/staricon/tianxie.png', '两人在一起时，双鱼的浪漫多情会让蝎子不可自拔，加上双鱼对蝎子的了解相当深刻，叫蝎子不能太认真都难。当然蝎座的魅力也相当吸引鱼座，不过爱情上的浪漫与温柔都是由鱼鱼主导出来的，再加上鱼座编剧的能力无人能比，蝎子不溺毙在鱼座的柔情似水下都很难。', '遇到蝎座，鱼鱼只要充分地表现出自己温柔天真的模样，保证蝎子很快被你的天真吸引，再来鱼鱼只要保持若即若离的距离，暗暗撩拨蝎座的心情就好，保证他们会自投罗网，而鱼座只需笑吟吟地在一旁喝茶等待，蝎子自然会排除万难来鱼鱼的身边赖着不走了!', '98', '0', '0');
INSERT INTO `mn_xz_recard` VALUES ('708', 'oV9VJ5GxZBX1kqdPviUFNjAv-ZMA', 'Lim', 'https://wx.qlogo.cn/mmopen/vi_32/DYAIOgq83epsDm8xWLeqSYv9x5TfsSSIWicAu3Z6b9q1bddSL3MNxW7R3otMAbxSNGzCpBhOQCjQyvgFhpb2Q2Q/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', '这是个小说般的组合，不但浪漫热情，还有点不切实际，这两个星座是大合的星座，不但对事情的看法相近，还能激起深刻的感觉，而且对感情的态度相同，一旦速配成功就很稳定。', '遇到鱼座蝎子可以不用那么激情，反而要在两个人之间培育出那种温暖隽永的感觉，才能让感情丰富的双鱼座觉得特别的眷恋，也才能留住鱼鱼的心，所以蝎子平常的酷样就可以收起来了，赶快摆出温柔多情的样子吧!这样才会让双鱼因为你的温柔而更加地深溺下去哦! ', '98', '0', '0');
INSERT INTO `mn_xz_recard` VALUES ('709', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5E9hza-3NMmz-lih3zvMcPk', '金梅', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKDTOfE48xIfjLsbrrOx0tAoFUnfdsKM6UP2TBem5JTq7KKRpVbvzWAcAYN8bDQh081VYnzWcMtiaw/132', '巨蟹', '../images/staricon/juxie.png', '这是一个深情的组合，这两个星座对爱情的态度都是非常专一的，一旦认定对方就不会想要改变，再加上两个人都非常重视感情的长久经营，所以能稳定下来的机率也是相当高的。', '面对蟹子，天蝎真的只要表现出自己的深情款款就好，光是这一点就足以让蟹子手忙脚乱，不知如何是好，表面上虽然倔强得很，但是实际上心里早已闹翻天，除非他们心有所属，不然大部分的蟹座都会因此感动不已，还可能偷偷动了心，蝎子只要坐在一边等着胜利到来即可。', '87', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('710', 'oV9VJ5E9hza-3NMmz-lih3zvMcPk', '金梅', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKDTOfE48xIfjLsbrrOx0tAoFUnfdsKM6UP2TBem5JTq7KKRpVbvzWAcAYN8bDQh081VYnzWcMtiaw/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5EPDHi0N56PQ4Xhtsap0GhU', '夜魇之眼', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK2eotCiaN2hxNNxA06QAziaLoicEw2fSA8QPCIibp0fpTXHbl9KRcASgYibCBI2sbyts9ZNhhk0eibWJ8Q/132', '天蝎', '../images/staricon/tianxie.png', '这是一个很专情的组合!可以说那些故事小说中大部分的描写专情的配对就是这种组合啦!这真的是一对足以让所有人眼红的情侣，美满深情到每个人都觉得是神话，真的非常难得哦! ', '这种超完美配对根本没什么谁强谁弱的问题，如果真的要说，那么天蝎算是这个配对中比较强势的角色，所以占了比较多的优势。主要是因为比起巨蟹座，蝎子显得有主见而且有行动力多了，他一定是比较主动的那一方，加上天生的神秘魅力，真是让人不得不倾倒的角色。', '87', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('711', 'oV9VJ5FF3EjWFyHPzXMHJ9IuNh-U', '��', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLUswfXViaNb3k2vQu8tm2DwmPn0ereCxPNwkOVM4fUpQyGdSUnP8HO3NOzGqGhiaPjyxzjXhXmuuwg/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5At0-Q-gEXlz6BJ4J6fxsvY', '朩渔', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLE53PEAXficYuolzboVWcuHMSv8IpEyShTZDdOH8bRDiaufgreAzmVZwn9S7hkWiav7iaRn9xgb3JCTg/132', '水瓶', '../images/staricon/shuiping.png', '瓶子对上瓶子算是完美的朋友组合，但是如果变成情人那就不太一定了，毕竟对两个爱好自由的人来说，当朋友才是最好的距离，不然在一起老是聚少离多，就没有在一起的必要了。', '瓶子对瓶子来说真是没什么好说的，反正都一样嘛，还有什么强弱之分呢?最重要的应该是两个人有没有地方可以一拍即合，再不满意也挑不出毛病来；一旦哪天不合适了，两人也没有什么死缠烂打的可能，反而是分手后还是朋友的代表。', '72', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('712', 'oV9VJ5At0-Q-gEXlz6BJ4J6fxsvY', '朩渔', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLE53PEAXficYuolzboVWcuHMSv8IpEyShTZDdOH8bRDiaufgreAzmVZwn9S7hkWiav7iaRn9xgb3JCTg/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5FF3EjWFyHPzXMHJ9IuNh-U', '��', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLUswfXViaNb3k2vQu8tm2DwmPn0ereCxPNwkOVM4fUpQyGdSUnP8HO3NOzGqGhiaPjyxzjXhXmuuwg/132', '水瓶', '../images/staricon/shuiping.png', '瓶子对上瓶子算是完美的朋友组合，但是如果变成情人那就不太一定了，毕竟对两个爱好自由的人来说，当朋友才是最好的距离，不然在一起老是聚少离多，就没有在一起的必要了。', '瓶子对瓶子来说真是没什么好说的，反正都一样嘛，还有什么强弱之分呢?最重要的应该是两个人有没有地方可以一拍即合，再不满意也挑不出毛病来；一旦哪天不合适了，两人也没有什么死缠烂打的可能，反而是分手后还是朋友的代表。', '72', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('713', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5C6uMIeuFFUtcY4lMRB9TyU', '', 'https://wx.qlogo.cn/mmopen/vi_32/lOGRGsBjaRyTDCvZentrjMbdJTAibNvOqFNvz7tXVgynj8KtBjNjHCTl4bjNUvJwYHNYabkIyNrVg2DcXeXBHZQ/132', '处女', '../images/staricon/chunv.png', '遇到处女时，双鱼全身散发的温柔气息和轻松姿态就很吸引人了。不妨多和处女座聊聊，处女座会发现你的特别，但是不要急着把感情表现出来，慢慢地加温和接近是你们最好的相处方式，不用太担心处女座人跑掉，因为了解他们的真的不多，这一点可是谁也比不让鱼座。', '真的没有谁强谁弱的问题，两人常常都分不清楚自己的想法和心情，总是百味难陈，而这种心情也只有这两个星座才能了解，所以可以说是知己。相处上处女的精明和双鱼的模糊是他们最不一样的地方，不过这正好有互补的功效，让处女们不会太紧张，双鱼们不会太迷糊。', '80', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('714', 'oV9VJ5C6uMIeuFFUtcY4lMRB9TyU', '', 'https://wx.qlogo.cn/mmopen/vi_32/lOGRGsBjaRyTDCvZentrjMbdJTAibNvOqFNvz7tXVgynj8KtBjNjHCTl4bjNUvJwYHNYabkIyNrVg2DcXeXBHZQ/132', '处女', '../images/staricon/chunv.png', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', '这是一个如梦似幻的组合，双方拥有相同和不同的层面，尤其在很多事的决定上都显得犹豫而挣扎，是一个能互相了解的配对，虽然这个组合变动的机会很大，但仍然算是速配组合。', '对待双鱼时要让他们感到你的照顾和同情，尤其是你们同样容易感伤的个性可以互相安慰取暖，只要让他们感受到被了解的喜悦，就很容易赢得他们的心。多与双鱼座聊天也是个办法，多开导安慰他，让他们在心灵上感到温暖，同时也在现实上得到稳定，双鱼座就离不开你了! ', '80', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('715', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5L6jYSBQrxRti9hs8RahctA', '透', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKCWGppqdPfRg3rRkKg588fziaLic4NysuhTtPfcRYFMqutSh9Le5gvDZp7lEibUYlRXWtqnO83Ths8w/132', '射手', '../images/staricon/sheshou.png', '遇到射手，双鱼们最好的方式就是摆出自己的万种风情，优雅、高贵、迷人，这些特质鱼鱼都有，就算不多但在欺敌上也够了!当然同时也要把自己的呆样收拾好，只要你表现够秀，不用你主动射手就迫不及待了，记得要充分发挥自己的幽默感，这是射手们最抵挡不住的。', '这是个非常美满的组合!尤其是对喜爱浪漫的人来说是天上地下无双的配对，既浪漫又热烈的剧情全在这种配对身上，叫身边的人不眼红都不行，是适合长久经营的配对。', '69', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('716', 'oV9VJ5L6jYSBQrxRti9hs8RahctA', '透', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKCWGppqdPfRg3rRkKg588fziaLic4NysuhTtPfcRYFMqutSh9Le5gvDZp7lEibUYlRXWtqnO83Ths8w/132', '射手', '../images/staricon/sheshou.png', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', '这个组合虽然没什么争执或火爆的场面，但是他们的确是不怎么相合的，因为两人星座一样懒，让他们没心情探究得太过仔细，所以速配的机会不大。', '遇到鱼座，射手们就得把自己的大而化之收拾一下，当个痴情的女主角或男主角，这种配合会让鱼鱼很感动，也会因为气氛的浪漫而义无反顾地跳下去，让你不费吹灰之力就拐到大笨鱼!之后，当然还要让鱼鱼尽量不要太早醒来，继续浪漫下去! ', '69', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('717', 'oV9VJ5MH14U8PWaTgZ91Z1vritBQ', 'Sammy', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKwPPpTPNlyr8ZoS9UDzzDntzBHxQrIleWjbUmmkevL3VrTsvphk6y7fcI17ZnBqd5sehuGh9HRPA/132', '摩羯', '../images/staricon/mojie.png', 'oV9VJ5PRKwQhjQIE2mLSWutoYu64', '慧康堂', 'https://wx.qlogo.cn/mmopen/vi_32/6tJXsPnFmTU3m3FAqHibNmWCbMicXt2cP66LiahricNqjNqIbIUicBHwoF61LeL7AHXPBZ7iayS2JKZbAOq2N0MKF4Eg/132', '天蝎', '../images/staricon/tianxie.png', '这是一个智商很高的组合，如果能找到共同的目标，那么成功是指日可待，不过就算没有共同的目标也不要成为敌人，通常这个组合是可以做到互相了解的，成功的话也会长久地经营下去。', '在这个组合中摩羯比蝎子笨。这俩人可都是高智商的，只不过蝎子想的和在乎的东西比摩羯少那么一点，如果蝎子只求个人的成功，那么摩羯座就是要创造子子孙孙的成功!这种志向蝎子是不会想的，所以摩羯算是老谋深算了。', '56', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('718', 'oV9VJ5PRKwQhjQIE2mLSWutoYu64', '慧康堂', 'https://wx.qlogo.cn/mmopen/vi_32/6tJXsPnFmTU3m3FAqHibNmWCbMicXt2cP66LiahricNqjNqIbIUicBHwoF61LeL7AHXPBZ7iayS2JKZbAOq2N0MKF4Eg/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5MH14U8PWaTgZ91Z1vritBQ', 'Sammy', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKwPPpTPNlyr8ZoS9UDzzDntzBHxQrIleWjbUmmkevL3VrTsvphk6y7fcI17ZnBqd5sehuGh9HRPA/132', '摩羯', '../images/staricon/mojie.png', '这个组合要不就是同谋，要不很可能是谍对谍的组合了!因为两个星座都非常深沉，所以常常在谋算别人，但是如果为敌的话那也是很精彩的。这个组合并不多，要看有没有缘分了!', '要解决摩羯一开始就要搞清楚他们的目标是什么，如果是对未来没有帮助的事情，摩羯可是不会做亏本的生意，所以只要先找出他们的目标，摩羯所有的行为模式就很好掌控了，尤其是他们很少改变目标，光凭这一点就足以让你反败为胜了，请保持冷静，好好计划吧!', '56', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('719', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5I8NKGTb8oZ4DsfcRp4YBDM', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK5HNDibbVG8BAUcEFhwBKAN45WpGQb7erpsBTdbCxGCu4WCeJIMxIiaRmhqZWia525icAZ4HhpX15R0g/132', '双子', '../images/staricon/shuangzi.png', '遇到双子座，鱼座一定要收起自己迷惘、混乱、困顿的一面，赶快摆出精明、美丽、充满神秘感的一面，保证双子座立刻就会自投罗网，对你的好奇心足以填平台湾海峡!当然，要记住，若即若离是很重要的，尤其不要让双子们看出你到底在想些什么，不然就前功尽弃了。', '这是一个怎么着都无法看好的组合，双子和双鱼座的那种相似又严重独立，让他们相处起来是鸡同鸭讲，到最后连他们自己都不了解为什么会在一起。是不太可能速配的星座组合。', '39', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('720', 'oV9VJ5I8NKGTb8oZ4DsfcRp4YBDM', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK5HNDibbVG8BAUcEFhwBKAN45WpGQb7erpsBTdbCxGCu4WCeJIMxIiaRmhqZWia525icAZ4HhpX15R0g/132', '双子', '../images/staricon/shuangzi.png', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', '这也是一个没什么交集的组合，两人对事情的看法反应不一样，连在乎的事情也不相同，在一起的机会是很小的，但如果有机会在一起，是很需要互相了解的，不然实在很难深入交往。', '其实双鱼座不太需要被了解，因为连他们自己也不知道自己在想什么，双子只要把他们的注意力从恍惚中拉回来就算是很厉害了，再来就细心一点，看看双鱼座喜欢或需要什么，让双鱼感动一番，刻不容缓的是有空要陪着他们聊聊心情和想法，不用很久就能得到双鱼座的依赖和倾心啦!', '39', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('721', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5K6penro7H-q3J2_e9ApcKw', '寒雪傲梅', 'https://wx.qlogo.cn/mmopen/vi_32/xKSibgCqo4qHmjKAXe3JysbpN95BB5ol2fIXU7jw0DvHqwvqicu25BsA7MoQF21dPVL07N3xHuZ0War4RvQY2zug/132', '狮子', '../images/staricon/shizi.png', '这个配对是很适合组合家庭的，因为这两个星座都非常传统，也都很重视家庭，所以只要两个人能在一起就会一起规划日后家庭的蓝图，是非常稳固的配对，适合一起步入婚姻。', '蟹子如果想摆平狮子真是易如反掌!道理很简单，因为蟹子很尊重狮子的决定，就算不同意也不会让狮子下不了台，给狮子留足了面子，不但保障了两个人的感情，也赢得了狮座对蟹子的喜爱与信任，真是何乐而不为呢?说真的，这才是最聪明的办法，蟹子一定要记住哦!', '67', '1', '1');
INSERT INTO `mn_xz_recard` VALUES ('722', 'oV9VJ5K6penro7H-q3J2_e9ApcKw', '寒雪傲梅', 'https://wx.qlogo.cn/mmopen/vi_32/xKSibgCqo4qHmjKAXe3JysbpN95BB5ol2fIXU7jw0DvHqwvqicu25BsA7MoQF21dPVL07N3xHuZ0War4RvQY2zug/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5H2spvTTIvcjYi-3CUoijrE', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLsfvygE7BM0vdMoGlxJmpuovo6uJcXTA4aicxNUQ8nicZicdGYcpLam20eic9VJEiaoFUK6LAIqA9QeJA/132', '巨蟹', '../images/staricon/juxie.png', '这是一个既稳定又能长久的组合，因为两人对于感情的看法是一样的，加上两个人都拥有希望稳定下来的个性，通常只要能速配成功就很难拆散他们了，尤其当狮子座是男方的时候。', '巨蟹座什么都不缺，就是缺乏安全感!这一点狮子座一定要明白，而且要拿来当护身符!只要狮子座能让蟹子感到安全无虑，就已经及格一半了，另一半还得加上幸福的保障!所以在巨蟹座面前狮子一定要专情，而且要一副很了解自己要做什么的模样，才能让多疑的蟹子解除心防。', '67', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('723', 'oV9VJ5DAKqP-sNzbYx6Ib7_BwgTg', '肉丸', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKE75UplojzkicUv9TWNFZibM1PjkUC2kaKNaOCRcZ6Ku2qSHNDBvdy1VItgib2pI4nC8ZvDkvkFxXmQ/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5DeXmihz_WuZwcoNfOtWIXQ', 'iMayDay', 'https://wx.qlogo.cn/mmopen/vi_32/7iaOF7VPbveFNgfBQ1p58L2We4L3Ueoiajl9Drl5p6ibuiaAz4oHS93Rtbg7KS9RWKXtbxib19Q3XHb1AiaTlHRMlcMA/132', '天蝎', '../images/staricon/tianxie.png', '这是一个不太乐观的组合，尤其是在两人都想主导感情的时候，最好的情况就是一方臣服，如若不然就只能让两人争出个输赢，战况惨烈是可以想像的，就算勉强在一起，长久下去的可能性也不高。', '面对蝎子时，摸清楚他的企图比什么都重要，小心仔细地观察一定会发现一些蛛丝马迹的!不过因为是天蝎对天蝎，所以最重要的是要让对方放下心防，以免两人都太紧张，那还有什么好玩的?等对方不注意的时候再出其不意攻其不备，这么一来就会出现令人意外的战果了! ', '20', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('724', 'oV9VJ5DeXmihz_WuZwcoNfOtWIXQ', 'iMayDay', 'https://wx.qlogo.cn/mmopen/vi_32/7iaOF7VPbveFNgfBQ1p58L2We4L3Ueoiajl9Drl5p6ibuiaAz4oHS93Rtbg7KS9RWKXtbxib19Q3XHb1AiaTlHRMlcMA/132', '天蝎', '../images/staricon/tianxie.png', 'oV9VJ5DAKqP-sNzbYx6Ib7_BwgTg', '肉丸', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKE75UplojzkicUv9TWNFZibM1PjkUC2kaKNaOCRcZ6Ku2qSHNDBvdy1VItgib2pI4nC8ZvDkvkFxXmQ/132', '天蝎', '../images/staricon/tianxie.png', '这是一个不太乐观的组合，尤其是在两人都想主导感情的时候，最好的情况就是一方臣服，如若不然就只能让两人争出个输赢，战况惨烈是可以想像的，就算勉强在一起，长久下去的可能性也不高。', '面对蝎子时，摸清楚他的企图比什么都重要，小心仔细地观察一定会发现一些蛛丝马迹的!不过因为是天蝎对天蝎，所以最重要的是要让对方放下心防，以免两人都太紧张，那还有什么好玩的?等对方不注意的时候再出其不意攻其不备，这么一来就会出现令人意外的战果了! ', '20', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('725', 'oV9VJ5MH14U8PWaTgZ91Z1vritBQ', 'Sammy', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKwPPpTPNlyr8ZoS9UDzzDntzBHxQrIleWjbUmmkevL3VrTsvphk6y7fcI17ZnBqd5sehuGh9HRPA/132', '摩羯', '../images/staricon/mojie.png', 'oV9VJ5HjgTUmmbzQ78bVxLJBC-ZI', 'Scoot', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTIQObCXrNjNVDsQ0Ja01z7vFicicytf3XDIVHQLpm8Kfpm8f0LY75Q4icj23t6InLKwDzHV7yibonibWjQ/132', '狮子', '../images/staricon/shizi.png', '这个组合实在不是让人太乐观的组合，因为俩人都有严重固执己见的倾向，没有打起来就已经算难得了，若还能找到两人的共识那可是值得普天同庆的，因此不一定能够长久下去。', '在这两个星座中摩羯座算是比较沉得住气的星座，所以比狮子座占优势一些，尤其在很多事情上摩羯都比狮座来得深思熟虑多了，而且摩羯座相当了解狮子，但是狮座就不一定了解摩羯心里在想什么了，所以如果要勾心斗角，狮子座大概没什么胜算，只能任人算计啦! ', '21', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('726', 'oV9VJ5HjgTUmmbzQ78bVxLJBC-ZI', 'Scoot', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTIQObCXrNjNVDsQ0Ja01z7vFicicytf3XDIVHQLpm8Kfpm8f0LY75Q4icj23t6InLKwDzHV7yibonibWjQ/132', '狮子', '../images/staricon/shizi.png', 'oV9VJ5MH14U8PWaTgZ91Z1vritBQ', 'Sammy', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKwPPpTPNlyr8ZoS9UDzzDntzBHxQrIleWjbUmmkevL3VrTsvphk6y7fcI17ZnBqd5sehuGh9HRPA/132', '摩羯', '../images/staricon/mojie.png', '这种组合是要看缘分的，因为这是两个完全不像的星座，几乎没什么共通之处，所以要让双方互相欣赏，是不太容易的事，但一旦在一起就可能产生互补效应，让两个人合作无间。', '基本上摩羯是一个很重实际的星座。什么叫很重实际?就是重视你有没有实力，以后会不会有\"钱\"途，和你在一起会不会有未来之类的，看起来真的是很不\"单纯\"吧!所以狮子座只要努力成为有出息的人，然后再加上一点专情之类的东西，就有机会赢得摩羯们的心了!', '21', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('727', 'oV9VJ5FF3EjWFyHPzXMHJ9IuNh-U', '��', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLUswfXViaNb3k2vQu8tm2DwmPn0ereCxPNwkOVM4fUpQyGdSUnP8HO3NOzGqGhiaPjyxzjXhXmuuwg/132', '水瓶', '../images/staricon/shuiping.png', 'oV9VJ5HlGgzpLqHfiEm62CsHJKWI', '.', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLcSfTyMs9mVJB5pv63icwT0OIeWtjUyaIuTIWkicficXQ9Jkew25FaQriaJrLdzqIybEt8a0Tm1L1FpQ/132', '处女', '../images/staricon/chunv.png', '这是一个充满吸引力的组合，不过这两个星座相差够远，所以争执也是难免，不过两人之间引力强大与否要看缘分够不够，足够的缘分会让你们的爱情长长久久，不会轻易褪色。', '这两个星座中处女座是比较吃香的，到底为什么呢?其实主要是因为他们比一般人来得复杂许多，这种神秘感让瓶子快要好奇死了，不一一探究那是枉费了水瓶的好奇心。所以两个人之间总是有不少的暧昧气氛，这种状况让瓶子简直被吸引得无法移开眼睛，好奇死了。', '35', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('728', 'oV9VJ5HlGgzpLqHfiEm62CsHJKWI', '.', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLcSfTyMs9mVJB5pv63icwT0OIeWtjUyaIuTIWkicficXQ9Jkew25FaQriaJrLdzqIybEt8a0Tm1L1FpQ/132', '处女', '../images/staricon/chunv.png', 'oV9VJ5FF3EjWFyHPzXMHJ9IuNh-U', '��', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLUswfXViaNb3k2vQu8tm2DwmPn0ereCxPNwkOVM4fUpQyGdSUnP8HO3NOzGqGhiaPjyxzjXhXmuuwg/132', '水瓶', '../images/staricon/shuiping.png', '这是一个互相之间有微妙吸引力的组合，虽然彼此完全没有任何的交集，但是却也因为如此而有着莫名的吸引力，让这两个星座之间相当的有缘分，但是未必是一个能够长久的组合。', '遇见水瓶这种怪星座，处女们只要懂得保持自己如梦似幻的微笑，就可以骗得瓶子们团团转了。千万不要去限制瓶子，不然他们的热情马上就会烟消云散，甚至还会迅速地逃离，这是个不太适合现实牵绊的组合，不妨就保持双方的神秘感，这样关系也才可以继续维持下去。', '35', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('729', 'oV9VJ5I8NKGTb8oZ4DsfcRp4YBDM', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK5HNDibbVG8BAUcEFhwBKAN45WpGQb7erpsBTdbCxGCu4WCeJIMxIiaRmhqZWia525icAZ4HhpX15R0g/132', '双子', '../images/staricon/shuangzi.png', 'oV9VJ5K7n3zO0LH-KD59PlgUrPlk', 'Hellecaster', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLO7CYPmg0n48GH9kiaOPnPU0RKrRayicq2QTcALI4TpTuBzjammczDF4JHSwOwQ7ou0NYTezIcOWww/132', '天秤', '../images/staricon/tianping.png', '这是一个相当顺利的组合哦!因为这两个星座不但个性相似，连相处的方法也很协调，只要在一起通常就能顺利地进行下去，加上同是爱好和平的风象星座，大概连架也吵不起来吧!', '其实没有差太多啦!只不过一向在唇枪舌剑中生存的双子，遇到了天性优雅和平的天秤，那真的是觉得相当新鲜了，而且秤子不会跟双子们斗嘴，因此能相处得非常愉快，甚至无话不谈，两人相处的气氛是既和平又融洽，如此一来，叫双子不喜欢秤子还真是有点难呢!', '82', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('730', 'oV9VJ5K7n3zO0LH-KD59PlgUrPlk', 'Hellecaster', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTLO7CYPmg0n48GH9kiaOPnPU0RKrRayicq2QTcALI4TpTuBzjammczDF4JHSwOwQ7ou0NYTezIcOWww/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5I8NKGTb8oZ4DsfcRp4YBDM', '', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTK5HNDibbVG8BAUcEFhwBKAN45WpGQb7erpsBTdbCxGCu4WCeJIMxIiaRmhqZWia525icAZ4HhpX15R0g/132', '双子', '../images/staricon/shuangzi.png', '这是一个非常顺利的组合，尤其是两个人的相处是轻松又愉快。这两个星座都是轻松的，觉得就算是天塌下来也轮不到他们来顶，这么志同道合，要说不速配成功都很困难了! ', '遇到双子，秤子也不用太努力啦!只要能够让双子聊个够、表演个够，他们就会觉得和你在一起很快乐，平常也不太需要去管他们，反正该出现的时候双子们也就会出现。\"放任\"是双子们最无法招架的绝招，试问哪个小孩不喜欢没人管的好日子?而且玩够了还是会回来的。', '82', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('731', 'oV9VJ5L6jYSBQrxRti9hs8RahctA', '透', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKCWGppqdPfRg3rRkKg588fziaLic4NysuhTtPfcRYFMqutSh9Le5gvDZp7lEibUYlRXWtqnO83Ths8w/132', '射手', '../images/staricon/sheshou.png', 'oV9VJ5MaXU9eCN0qYyU5_G93Xotw', 'Seven', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaEIpdibtjg7WU21N5OCMKdmnf4DHvJUXCCxUuM1IMN1szRC6mUx0uBo5EeOWribvWtM2xia24WenhcMCw/132', '天秤', '../images/staricon/tianping.png', '这个组合互相欣赏的成分比较多一些，不会有太激情的演出，常常都是从朋友转型成恋人的，所以互相的了解很深，相处融洽，但没有太多热情。这个配对也算是很般配的组合之一。', '这个配对中射手座比秤子吃香一些，因为射手们的我行我素是很容易吸引秤子的，尤其是秤子一向没有太多意见，而射手偏偏是最有意见的那个，导致大部分的事情都是射手在做主，因为也很依赖有人帮他们做决定的状况，到后来天秤座反而变得有些依赖射手座了。', '73', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('732', 'oV9VJ5MaXU9eCN0qYyU5_G93Xotw', 'Seven', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaEIpdibtjg7WU21N5OCMKdmnf4DHvJUXCCxUuM1IMN1szRC6mUx0uBo5EeOWribvWtM2xia24WenhcMCw/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5L6jYSBQrxRti9hs8RahctA', '透', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKCWGppqdPfRg3rRkKg588fziaLic4NysuhTtPfcRYFMqutSh9Le5gvDZp7lEibUYlRXWtqnO83Ths8w/132', '射手', '../images/staricon/sheshou.png', '这是一个以朋友为基调的组合，所以稍嫌不够火辣，不过在互相了解或互相放纵这一点上绝对是配合无间的，彼此都拥有足够的个人自由，如果能速配成功的话是很值得羡慕的。', '射手的自由和自我程度比秤子要来得彻底一点，所以他们不像秤子老是在别人和自己之间矛盾挣扎，反而任性得很直接。这一点就足够秤子欣赏了，加上他们天生的热情和浪漫，发挥一点就能感动到秤子本质上的浪漫倾向，事实上对射手而言这根本只是举手之劳罢了。', '73', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('733', 'oV9VJ5I3R4tFF6TdgO-t5-3wkvxY', '温柔冰', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKzfYib4XrwRRP7tUzQjeeIYr47AjN8BWWFeS5kL8lgJB8uuQKrJu0HZnYC666f9ecwM7AQF2rfvzg/132', '天秤', '../images/staricon/tianping.png', 'oV9VJ5DUU_bT5oiEJHVEjeSSet8Y', '��   王安妮.          面膜大王', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaEL3ibVYoRwuoG4jy1zbfxRKJ321BEaqia6oy5KM2wOjIkibMl0nQV1C5UHE0K3IKYPRO8UgB0AAIW0Mw/132', '白羊', '../images/staricon/baiyang.png', '这是一个会互相吸引的配对，别人看起来两个人虽然天差地别，不过事实上他们重视的事情还是有很多是相同的。所以只要没有恶劣到如敌人般仇视，成为亲密战友的日子则指日可待!', '秤子要对付白羊其实不会很难的，因为羊羊不是个很\"不讲理\"的星座，该讲道理的时候他们也听得懂，只是不太能理解就是了。那秤子就做给他们看，这样羊羊就会理解，只是时间要久一点。总之，秤子只要气定神闲就好了，不管羊羊到底抓狂到什么地步，安心等他气消回来吧!', '68', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('734', 'oV9VJ5DUU_bT5oiEJHVEjeSSet8Y', '��   王安妮.          面膜大王', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaEL3ibVYoRwuoG4jy1zbfxRKJ321BEaqia6oy5KM2wOjIkibMl0nQV1C5UHE0K3IKYPRO8UgB0AAIW0Mw/132', '白羊', '../images/staricon/baiyang.png', 'oV9VJ5I3R4tFF6TdgO-t5-3wkvxY', '温柔冰', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTKzfYib4XrwRRP7tUzQjeeIYr47AjN8BWWFeS5kL8lgJB8uuQKrJu0HZnYC666f9ecwM7AQF2rfvzg/132', '天秤', '../images/staricon/tianping.png', '这是个很不一样但又能互相协调的配对，两个人有着密不可分的关系，但最重要的是必须彼此尊重，感情才能长久维持下去。通常这种组合的缘分都很深厚，值得好好花心力经营。', '羊羊的自我和秤子的随和正好相反，不过羊座要注意秤子是个重视公平的星座，如果你希望秤子为你做什么，那么你也要让他们觉得你为他做了什么，不要一味地任性，久了秤子是不吃你这一套的!没事多听听秤子的意见，就算他不说也要问，你才能知道如何跟他相处。', '68', '1', '0');
INSERT INTO `mn_xz_recard` VALUES ('735', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', 'oV9VJ5FcPipHD0XZNUuszYTveo3I', 'Bart', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTL28xLic0mUvTSv8gZFqfwqswZNiblZMb7nJAcfd1iber10sUpAwoo4r7JSsm1ia4ibabJDHicPPNcMkB3Q/132', '巨蟹', '../images/staricon/juxie.png', '遇到蟹子，鱼座惟一要做的就是把蟹子心底想的摸个透彻，因为蟹子很需要别人了解和谅解，而鱼座正好具有这种同情心旺盛的特质，加上似水的柔情，不用一时三刻，蟹子不上钩都难。不过也是因为这两个星座天生有互相吸引的特质，所以实在是不用动太多的脑筋。', '一个这么深情的组合还有什么好比的?不过若是在爱情的依赖度上，鱼鱼对巨蟹座有着莫名的依赖感，可能是因为巨蟹具有母亲本能吧!这种包容和体贴，是鱼座软弱时最好的避风港，鱼鱼其实是很脆弱的，受不得大风大浪，蟹子的两个大螯正好提供了他们最好的保证。', '98', '0', '0');
INSERT INTO `mn_xz_recard` VALUES ('736', 'oV9VJ5FcPipHD0XZNUuszYTveo3I', 'Bart', 'https://wx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTL28xLic0mUvTSv8gZFqfwqswZNiblZMb7nJAcfd1iber10sUpAwoo4r7JSsm1ia4ibabJDHicPPNcMkB3Q/132', '巨蟹', '../images/staricon/juxie.png', 'oV9VJ5DNIrc-g79qxMynDZvsSh_M', 'Akasma', 'https://wx.qlogo.cn/mmopen/vi_32/PiajxSqBRaELt5Q9PStV37iceNQwZOEqPrkqUiaVXovbV04vUZEcGpkymZ2GETwk4JqcnvtLdCv0IDasjMgrxhoZg/132', '双鱼', '../images/staricon/shuangyu.png', '这可是一个超级浪漫的组合，他们不切实际的程度足以媲美\"灰姑娘\"的际遇，这个配对真的很容易变得不切实际，有可能有了爱情就不要面包，不过却是非常非常的惟美浪漫哦! ', '蟹子的全心付出会让鱼座非常感动，觉得无以为报，所以蟹子要做的事就是不断付出自己丰沛的爱心，让鱼座溺死在里面，连翻身的机会都没有，加上鱼座对巨蟹日渐变得依赖，这样一来鱼鱼就再也跑不掉了，而这个美妙的爱情配对，自然就会有幸福美满的结局出现了! ', '98', '0', '0');
